import fs from 'fs';

/**
 * Our base Cache implementation
 * Extend this class with new implementations to create different cache types (in-memory, database, file system etc.)
 * @class
 */
export default class CacheBase {
  /**
     * @param {Object} options
     */
  constructor(options = {}) {
    // stack up multiple cache wraps so they wait for a single request to finish
    this.pendingCacheWraps = {};

    this.failedCacheWraps = {};

    this.pendingLocks = [];
    this.hasPendingLocks = false;
  }

  /**
     * Internal implementation of Get()
     * @param {string} key Unique key name for this cache entry
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     * @abstract
     * @private
     */
  async _get(key) {
    throw new Error('Missing Implementation CacheBase::_get(key)');
  }

  /**
     * Internal implementation of Set()
     * @param {string} key Unique key name for this cache entry
     * @param {Object} object Data to be set
     * @abstract
     * @private
     */
  async _set(key, object) {
    throw new Error('Missing Implementation CacheBase::_set(key, object)');
  }

  /**
     * Internal operation to delete a key
     * @param {string} key Key name to delete
     * @abstract
     * @private
     */
  async _del(key) {
    throw new Error('Missing Implementation CacheBase::_del(key)');
  }

  /**
     * Internal implementation of getKeys()
     * @param {string} prefix
     * @abstract
     * @private
     */
  async _getKeys(prefix) {
    throw new Error('Missing Implementation CacheBase::_getKeys(prefix)');
  }

  /**
     * Get a cached object
     * @param {string} key Unique key name for this cache entry
     * @param {boolean} [getFullObject] Get the full cache entry, including expiry time, even if expired
     * @param {boolean} [force=false] Force set, bypassing transaction blocks
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     */
  async get(key, getFullObject = false, force = false) {
    if (!force) {
      await this.blockOnPendingTransactions();
    }

    const now = +new Date();

    // then use our internal cache if we haven't got the value stored locally
    const cacheValue = await this._get(key);
    if (cacheValue !== undefined) {
      if (getFullObject) {
        return cacheValue;
      }

      if (cacheValue.expires >= now) {
        return cacheValue.value;
      }
    }

    return undefined;
  }

  /**
     * Set a key in our cache
     * @param {string} key Unique key name for this cache entry
     * @param {Object} value
     * @param {(Function|number)} [ttl=3600000] How long the cache entry should last in milliseconds
     * @param {boolean} [force=false] Force set, bypassing transaction blocks
     *  Can be a number or a function that will return a number
     *  Default 1 hour
     */
  async set(key, value, ttl = 3600000, force = false) {
    // resolve our cache time
    let cacheTime = ttl;
    // if our cache time input is a function, resolve it and store the result (in milliseconds)
    if (typeof cacheTime === 'function') {
      cacheTime = await cacheTime();
    }

    if (!force) {
      await this.blockOnPendingTransactions();
    }

    if (cacheTime < 0) {
      // delete key if ttl is negative
      await this._del(key);
    } else {
      // call the private _Set implementation to actually set the key
      await this._set(key, {
        value,
        expires: (+new Date()) + cacheTime,
      });
    }
  }

  /**
     * A helper "wrap" function that will return a cached value if present
     *  This will call the supplied function to fetch it if the value isn't present in the cache
     * @param {string} key Unique key name for this cache entry
     * @param {function} fn Fetch function that will be called if the cache entry is not present
     * @param {(function|number)} [ttl] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     */
  async wrap(key, fn, ttl) {
    // if another system is already wrapping this key, return it's pending Promise
    if (this.pendingCacheWraps[key] !== undefined) {
      return this.pendingCacheWraps[key];
    }

    // wrap all await calls in another Promise that we store
    //  this allows multiple calls to Wrap to stack up, and they all get the same result
    this.pendingCacheWraps[key] = new Promise(async (resolve) => {
      // try and fetch the cached value
      const cachedValue = await this.get(key, true);

      // if not in our cache, call the supplied fetcher function
      if (cachedValue !== undefined) {
        // check timestamp to see if value is still valid
        const now = +new Date();
        if (cachedValue.expires > now) {
          // it is! return it!
          return resolve(cachedValue.value);
        }
        // it isn't! fall through and run our wrap function to get new data
      }

      let error = null;
      try {
        const newValue = await fn();

        // set the new value in our cache
        this.failedCacheWraps[key] = 0;
        await this.set(key, newValue, ttl);
        return resolve(newValue);
      } catch (e) {
        // store in case we want to throw this later
        error = e;
        console.error(`Error caching value ${key}`, e);
      }

      if (this.failedCacheWraps[key] === undefined) {
        this.failedCacheWraps[key] = 0;
      }
      this.failedCacheWraps[key]++;

      // failed! store old data briefly, then return old data back
      await this.set(key, cachedValue?.value, 1000 * 30); // try again in 30 seconds
      if (this.failedCacheWraps[key] > 5) {
        // report after multiple failures
        if (error !== null) {
          // throw the actual error if we had one
          throw error;
        }
        throw new Error(`Failed to resolve wrap function for cache key ${key}`);
      }
      return resolve(cachedValue?.value);
    });
    const cachedValue = await this.pendingCacheWraps[key];
    this.pendingCacheWraps[key] = undefined;

    // if debugging, store data to disk
    if (process.env.DEBUG_WRITE) {
      if (!fs.existsSync('./debug_cache')) {
        fs.mkdirSync('./debug_cache');
      }
      fs.writeFileSync(`debug_cache/${key.replace(/[^a-zA-Z0-9_-]/g, '')}.json`, JSON.stringify(cachedValue, null, 2));
    }

    // return the fetched or calculated value
    return cachedValue;
  }

  /**
   * Get an array of all the cached keys matching the supplied prefix
   * @param {string} [prefix='']
   * @return {array<string>}
   */
  async getKeys(prefix = '') {
    return this._getKeys(prefix);
  }

  /**
   * Run a transaction.
   * All other queries are halted while this is running
   * @param {function} func
   * @param {function} [keyModifierFunc=null] Optional function that translates incoming keys
   */
  async runTransaction(func, keyModifierFunc = null) {
    // function to convert key names (used by scopedCache)
    const keyNamer = keyModifierFunc ? keyModifierFunc : (key) => key;

    return new Promise(async (resolve) => {
      this.pendingLocks.push(async () => {
        // call function with a helper stub with core functions
        //  use this to make requests inside transaction
        await func({
          get: async (key, getFullObject) => {
            return this.get(keyNamer(key), getFullObject, true);
          },
          set: async (key, value, ttl) => {
            return this.set(keyNamer(key), value, ttl, true);
          },
        });
        resolve();
      });

      // process all our pending locks
      if (!this.hasPendingLocks) {
        this.hasPendingLocks = true;
        while (this.pendingLocks.length > 0) {
          const currLock = this.pendingLocks.shift();
          await currLock();
        }
        this.hasPendingLocks = false;
      }
    });
  }

  /**
   * Block if we have any pending transactions
   * @return {Promise}
   */
  async blockOnPendingTransactions() {
    if (this.hasPendingLocks) {
      return new Promise((resolve) => {
        this.pendingLocks.push(resolve);
      });
    }
  }
}

import CacheBase from './cacheBase.js';
import levelup from 'levelup';
import leveldown from 'leveldown';
import memdown from 'memdown';
import path from 'path';

const memory = false;

/**
 * A cache implementation using LevelDown
 * @extends CacheBase
 * @class
 */
export default class CacheLevel extends CacheBase {
  /**
   * Create new Memory cache object
   * @param {object} options
   */
  constructor(options = {}) {
    super(options);

    // setup our Level database
    if (memory) {
      this.db = levelup(memdown());
    } else {
      this.db = levelup(leveldown(path.join(process.cwd(), 'db.cache')));
    }
  }

  /**
   * @inheritdoc
   */
  async _get(key) {
    try {
      const cacheEntry = await this.db.get(key);
      if (cacheEntry !== undefined) {
        return JSON.parse(cacheEntry);
      }
    } catch (err) {
      // ignore NotFoundError, throw any other errors back up the chain
      if (err.name !== 'NotFoundError') {
        throw err;
      }
    }

    return undefined;
  }

  /**
   * @inheritdoc
   */
  async _set(key, object) {
    await this.db.put(key, JSON.stringify(object));
  }

  /**
   * @inheritdoc
   */
  async _del(key) {
    await this.db.del(key);
  }

  /**
   * @inheritdoc
   */
  async _getKeys(prefix) {
    return new Promise((resolve) => {
      const keys = [];
      const keyStream = this.db.createKeyStream();
      keyStream.on('data', (data) => {
        if (data.indexOf(prefix) === 0) {
          keys.push(data.toString());
        }
      });
      keyStream.on('end', () => {
        return resolve(keys);
      });
    });
  }
}

import CacheBase from './cacheBase.js';
import path from 'path';

import {open} from 'lmdb';

/**
 * A cache implementation using LMDB
 * @extends CacheBase
 * @class
 */
export default class CacheLmdb extends CacheBase {
  /**
   * Create new Memory cache object
   * @param {object} options
   */
  constructor(options = {}) {
    super(options);

    // setup our database
    this.db = open({
      path: path.join(process.cwd(), 'db.datacache'),
      compression: true,
    });
  }

  /**
   * @inheritdoc
   */
  async _get(key) {
    return this.db.get(key);
  }

  /**
   * @inheritdoc
   */
  async _set(key, object) {
    await this.db.put(key, object);
  }

  /**
   * @inheritdoc
   */
  async _del(key) {
    await this.db.remove(key);
  }

  /**
   * @inheritdoc
   */
  async _getKeys(prefix) {
    const keys = [];
    for (const key of this.db.getKeys()) {
      if (!prefix || key.startsWith(prefix)) {
        keys.push(key);
      }
    }
    return keys;
  }
}

import CacheBase from './cacheBase.js';

/**
 * A basic in-memory cache implementation
 * @extends CacheBase
 * @class
 */
export default class CacheMemory extends CacheBase {
  /**
   * Create new Memory cache object
   * @param {object} options
   */
  constructor(options) {
    super(options);

    this.cache = {};
  }

  /**
   * @inheritdoc
   */
  async _get(key) {
    const cacheEntry = this.cache[key];
    if (cacheEntry !== undefined) {
      return cacheEntry;
    }

    return undefined;
  }

  /**
   * @inheritdoc
   */
  async _set(key, object) {
    this.cache[key] = object;
  }

  /**
   * @inheritdoc
   */
  async _del(key) {
    if (this.cache[key]) {
      delete this.cache[key];
    }
  }

  /**
   * @inheritdoc
   */
  async _getKeys(prefix) {
    return Object.keys(this.cache).filter((key) => {
      return key.indexOf(prefix) === 0;
    });
  }
}

import {getCache} from '../cache.js';
import {reusePromiseForever} from '../reusePromises.js';

/**
 * A wrapper class for accessing the cache.
 * Prefixes a string before each key to avoid conflicts.
 */
export class ScopedCache {
  /**
   * Create a new ScopedCache object by passing in the key prefix descired
   * @param {string} keyPrefix
   * @param {number} [version] Cache version, bump this to invalidate existing cache entries for a scope
   * @param {object} [cacheObject=null] Manually supply a cache object to use
   */
  constructor(keyPrefix, version = 0, cacheObject = null) {
    this.prefix = keyPrefix;
    this.version = version;

    this.cache = null;
    this.cacheOverride = cacheObject;
  }

  /**
   * Initialise the cache for this scope
   */
  async initCache() {
    return await reusePromiseForever(this, this._initCache);
  }

  /**
   * Internal cache initialisation
   */
  async _initCache() {
    if (this.cache) {
      return this.cache;
    }

    if (!!this.cacheOverride) {
      this.cache = this.cacheOverride;
    } else {
      this.cache = await getCache();
    }

    // check and flush cache if version mismatch
    const cacheVersion = await this.cache.get(this.generateScopedKey('%%version%%'));
    if (cacheVersion !== undefined && cacheVersion != this.version) {
      // find all cache entries with this scope, and remove them
      const keys = await this.cache.getKeys(`${this.prefix}_`);

      await Promise.allSettled(keys.map((key) => {
        // set expire date to 1 millisecond ago (this basically deletes it)
        return this.cache.set(key, {}, -1);
      }));
    }

    // set our new cache version with a very very long ttl
    await this.cache.set(this.generateScopedKey('%%version%%'), this.version, Number.MAX_SAFE_INTEGER);
    this._initCachePromise = null;

    return this.cache;
  }

  /**
   * Generate a scoped key by adding our prefix to the incoming key
   * @param {string} inKey
   * @return {string} Scoped key
   */
  generateScopedKey(inKey) {
    return `${this.prefix}_${inKey}`;
  }

  /**
     * Get a cached object
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {boolean} [getFullObject] Get the full cache entry, including expiry time, even if expired
     * @param {boolean} [force=false] Force set, bypassing transaction blocks
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     */
  async get(key, getFullObject = false, force = false) {
    return this.getGlobal(this.generateScopedKey(key), getFullObject, force);
  }

  /**
     * Get a cached object from the global cache (skipping the scope prefix)
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {boolean} [getFullObject] Get the full cache entry, including expiry time, even if expired
     * @param {boolean} [force=false] Force set, bypassing transaction blocks
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     */
  async getGlobal(key, getFullObject = false, force = false) {
    const cache = await this.initCache();
    return cache.get(key, getFullObject, force);
  }

  /**
     * Set a key in our cache
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {Object} value
     * @param {(Function|number)} [ttl=3600000] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     *  Default 1 hour
     */
  async set(key, value, ttl = 3600000) {
    return this.setGlobal(this.generateScopedKey(key), value, ttl);
  }

  /**
     * Set a key in our global cache, skipping the scoped prefix
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {Object} value
     * @param {(Function|number)} [ttl=3600000] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     *  Default 1 hour
     */
  async setGlobal(key, value, ttl = 3600000) {
    const cache = await this.initCache();
    return cache.set(key, value, ttl);
  }

  /**
     * A helper "wrap" function that will return a cached value if present
     *  This will call the supplied function to fetch it if the value isn't present in the cache
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {function} fn Fetch function that will be called if the cache entry is not present
     * @param {(function|number)} [ttl] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     */
  async wrap(key, fn, ttl) {
    return this.wrapGlobal(this.generateScopedKey(key), fn, ttl);
  }

  /**
     * A helper "wrap" function that will return a cached value if present (in the global scope)
     *  This will call the supplied function to fetch it if the value isn't present in the cache
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {function} fn Fetch function that will be called if the cache entry is not present
     * @param {(function|number)} [ttl] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     */
  async wrapGlobal(key, fn, ttl) {
    const cache = await this.initCache();
    return cache.wrap(key, fn, ttl);
  }

  /**
   * Run a series of functions in a single transaction
   * @param {functions} func
   * @return {promise}
   */
  async runTransaction(func) {
    const cache = await this.initCache();
    return cache.runTransaction(func, (key) => {
      return this.generateScopedKey(key);
    });
  }

  /**
   * Block if we have any pending transactions
   * @return {Promise}
   */
  async blockOnPendingTransactions() {
    const cache = await this.initCache();
    return cache.blockOnPendingTransactions();
  }

  /**
   * Get an array of all the cached keys matching the supplied prefix
   * @param {string} [prefix='']
   * @return {array<string>}
   */
  async getKeys(prefix = '') {
    const cache = await this.initCache();
    return (await cache.getKeys(`${this.prefix}_${prefix}`)).map((x) => {
      // return keys without our scoped cache prefix
      return x.slice(this.prefix.length + 1);
    });
  }
}

export default ScopedCache;

/**
 * Cache provider
