import ConfigBase from '../configBase.js';
import sieve from './sieve.js';
import {reusePromise, reusePromiseForever} from '../reusePromises.js';
import HTTP from './http.js';
import Cache from '../cache/scopedCache.js';

const Databases = {};

/**
 * A class that handles fetching and storing data for a resort/park
 */
export class Database extends ConfigBase {
  /**
   * Construct a new Database object
   * @param {object} options
   */
  constructor(options = {}) {
    options.useragent = options.useragent || null;

    super(options);

    this.cache = new Cache(this.constructor.name, this.config.cacheVersion || 0);

    this.http = new HTTP();
    if (this.config.useragent) {
      this.http.useragent = this.config.useragent;
    }

    this.http.injectForDomain({hostname: {$exists: true}}, (method, url) => {
      this.log(method, url);
    });
  }

  /**
   * Pretty print log for this database
   */
  log(...args) {
    console.log(`[\x1b[32m${this.constructor.name}\x1b[0m]`, ...args);
  }

  /**
   * Get singleton of this class type
   * @param {object} [options] Options to pass to new instance.
   * Will only be used if the instance doesn't already exist
   * @return {Database}
   */
  static get(options = {}) {
    const className = this.name;
    if (Databases[className] === undefined) {
      Databases[className] = new this(options);
    }
    return Databases[className];
  }

  /**
   * Initialise this database object
   */
  async init() {
    // only ever call _init once
    await reusePromiseForever(this, this._init);
  }

  /**
   * Internal init function, override this in child classes for functionality
   * @abstract
   */
  async _init() {}

  /**
   * Get entities from this database. Optionally filtering by some conditions
   * @param {object} [filter]
   */
  async getEntities(filter = {}) {
    await this.init();

    const entities = await this.cache.wrap('entities', () => {
      return reusePromise(this, this._getEntities);
    }, 1000 * 60); // cache for a minute for faster access

    const filterFn = sieve(filter);
    return entities.filter(filterFn);
  }

  /**
   * Find a single entity from this database
   * @param {object} [filter]
   */
  async findEntity(filter = {}) {
    const ents = await this.getEntities();
    const filterFn = sieve(filter);
    return ents.find(filterFn);
  }

  /**
   * Return all entities for this resort/park
   * @abstract
   * @return {array<object>}
   */
  async _getEntities() {
    throw new Error(`Database class ${this.constructor.name} missing _getEntities() function`);
  }

  /**
   * Get an entity object from it's ID
   * @param {string} entityId Entity ID
   */
  async getEntitiyById(entityId) {
    return await this.findEntity({
      id: `${entityId}`,
    });
  }
}

export default Database;

import EventEmitter from 'events';
import objHash from 'object-hash';
import moment from 'moment-timezone';

import Cache from '../cache/scopedCache.js';
import {reusePromiseForever} from '../reusePromises.js';
import {entityType} from './parkTypes.js';
import {getLiveDataErrors} from './livedata.js';
import HTTP from './http.js';

import {parseConfig} from '../configBase.js';
import {validateEntitySchedule} from './scheduledata.js';

/**
 * Return functions available on the supplied object as an array of strings
 * @param {object} obj
 * @return {Array<string>} array of function names
 */
const getMethods = (obj) => {
  const properties = new Set();
  let currentObj = obj;
  do {
    Object.getOwnPropertyNames(currentObj).map((item) => properties.add(item));
  } while ((currentObj = Object.getPrototypeOf(currentObj)));
  return [...properties.keys()].filter((item) => typeof obj[item] === 'function');
};

/**
 * Meta-programming commands
 * Functions can declare tags to add extra functionality to their behaviour.
 * This is a replacement for decorators until they actually exist.
 */
const metaCommands = {
  // @cache|minutesToCache
  cache: function(fnName, args) {
    const originalFunction = this[fnName].bind(this);

    this[fnName] = async (...originalFunctionArgs) => {
      let funcCacheName = `metacache_${fnName}`;
      if (originalFunctionArgs.length > 0) {
        funcCacheName = `metacache_${fnName}_${originalFunctionArgs.map((x) => JSON.stringify(x)).join(',')}`;
      }

      // figure out cache time from args[0]
      let cacheTime = 1000 * 60 * 5; // default to 5 minutes
      if (args.length > 0) {
        // look for units of time
        const timeUnit = args[0].match(/^(\d+)([mhd])$/);
        if (timeUnit) {
          const time = parseInt(timeUnit[1], 10);
          const unit = timeUnit[2];
          switch (unit) {
            case 'm':
              cacheTime = 1000 * 60 * time;
              break;
            case 'h':
              cacheTime = 1000 * 60 * 60 * time;
              break;
            case 'd':
              cacheTime = 1000 * 60 * 60 * 24 * time;
              break;
          }
        } else {
          // if no unit of time is supplied, assume minutes
          cacheTime = parseInt(args[0], 10) * 1000 * 60;
        }
      }

      // replace original function with a cache wrap
      return this.cache.wrap(
          // try and make a unique name based on our function name
          //  to store in cache
          funcCacheName,
          // call original function if value not in cache
          async () => {
            return originalFunction(...originalFunctionArgs);
          },
          // @cache takes argument in minutes
          parseInt(args[0], 10) * 1000 * 60,
      );
    };
  },
};

/**
 * The base Destination object
 */
export class Destination extends EventEmitter {
  /**
   * Construct a new empty Destination object
   * @param {object} options
   */
  constructor(options = {}) {
    super();

    // add class name to our $env options
    options.configPrefixes = [this.constructor.name].concat(
        options.configPrefixes || [],
    );

    // debug callback to list environment variables
    if (options?.envCallback) {
      const keys = Object.keys(options);
      const prefix = this.constructor.name;
      keys.map((x) => {
        options?.envCallback(prefix, x);
      });
    }

    // parse config from ENV by combining with options
    const config = parseConfig(options);
    this.config = config;

    this._destinationEntityObject = null;

    // create a new cache object for this destination
    this.cache = new Cache(this.constructor.name, this.config.cacheVersion || 0);

    this._allEntities = null;
    this._allEntitiesLastStash = 0;

    this.http = new HTTP();
    if (this.config.useragent) {
      this.http.useragent = this.config.useragent;
    }

    if (!this.config.timezone) {
      throw new Error(`All destination objects must have a timezone! ${this.constructor.name}`);
    }

    // debug log all HTTP requests
    this.http.injectForDomain({hostname: {$exists: true}}, (method, url) => {
      this.log(method, url);
    });

    // get list of all class functions for some meta programming
    const funcs = getMethods(this);
    funcs.forEach((funcName) => {
      // skip constructor
      if (funcName === 'constructor') return;

      // get function as a string
      const funcStr = this[funcName].toString();
      // match any lines that contain only a string starting with @
      //  eg. '@cache()';
      // TODO - match multiple times for complex meta functions
      const match = /^\s*(['"`])\s*@([^'"`]+)\1;?/mg.exec(funcStr);
      if (match) {
        const splits = match[2].split('|');
        // look for matching meta function
        const metaFn = metaCommands[splits[0]];
        if (!metaFn) {
          return;
        }

        // console.log(`Setting up ${splits[0]} for ${funcName}...`);

        // call meta function
        metaFn.call(this, funcName, splits.slice(1));
      }
    });
  }

  /**
   * Debug log
   * @param  {...any} args Message to debug log
   */
  log(...args) {
    console.log(`[\x1b[32m${this.constructor.name}\x1b[0m]`, ...args);
  }

  /**
   * Initialise the object. Will only be initialised once
   */
  async init() {
    await reusePromiseForever(this, this._init);
  }

  /**
   * Instance implementation of init, implement this function for a single-execution of init()
   */
  async _init() { }

  /**
   * Return the current time for this destination in its local timezone
   * @return {moment}
   */
  getTimeNowMoment() {
    return moment().tz(this.config.timezone);
  }

  /**
   * Return the current time for this destination in its local timezone
   * @return {string}
   */
  getTimeNow() {
    return this.getTimeNowMoment().format();
  }

  /**
   * Set local live data cache for an entity
   * @param {string} id
   * @param {object} data
   * @param {object} [lock] Optional lock file to use for transactions
   * @private
   */
  async setLiveCache(id, data, lock) {
    const cache = lock ? lock : this.cache;
    const cacheTime = 1000 * 60 * 60 * 24 * 30 * 6; // 6 months
    await cache.set(`${id}_live`, data, cacheTime);
    // generate hash and store separately
    await cache.set(`${id}_livehash`, data ? objHash(data) : undefined, cacheTime);
  }

  /**
   * Get the locally stored live data for an ID
   * @param {string} id
   * @param {object} [lock]
   * @return {object}
   */
  async getLiveCache(id, lock) {
    const cache = lock ? lock : this.cache;
    return cache.get(`${id}_live`);
  }

  /**
   * Get the hash of a stored live data of an entity
   * @param {string} id
   * @param {object} [lock] Optional lock file to use for transactions
   * @private
   */
  async getLiveHash(id, lock) {
    const cache = lock ? lock : this.cache;
    return await cache.get(`${id}_livehash`);
  }

  /**
   * Build a generic base entity object
   * @param {object} data
   * @return {object}
   */
  buildBaseEntityObject(data) {
    const entity = {
      timezone: this.config.timezone,
    };

    return entity;
  }

  /**
   * Build the destination entity representing this destination
   */
  async buildDestinationEntity() {
    throw new Error('buildDestinationEntity() needs an implementation', this.constructor.name);
  }

  /**
   * Build the park entities for this destination
   */
  async buildParkEntities() {
    throw new Error('buildParkEntities() needs an implementation', this.constructor.name);
  }

  /**
   * Build the attraction entities for this destination
   */
  async buildAttractionEntities() {
    throw new Error('buildAttractionEntities() needs an implementation', this.constructor.name);
  }

  /**
   * Build the show entities for this destination
   */
  async buildShowEntities() {
    throw new Error('buildShowEntities() needs an implementation', this.constructor.name);
  }

  /**
   * Build the restaurant entities for this destination
   */
  async buildRestaurantEntities() {
    throw new Error('buildRestaurantEntities() needs an implementation', this.constructor.name);
  }

  /**
   * Get the Entity object for this destination
   * @return {Object}
   */
  async getDestinationEntity() {
    await this.init();

    // TODO - cache this?
    if (!this._destinationEntityObject) {
      this._destinationEntityObject = await this.buildDestinationEntity();
    }
    return this._destinationEntityObject;
  }

  /**
   * Get all entities belonging to this destination.
   */
  async getAllEntities() {
    const minCacheTime = +new Date() - (1000 * 60 * 5); // refresh every 5 minutes
    if (this._allEntities && this._allEntitiesLastStash > minCacheTime) {
      return this._allEntities;
    }

    // TODO - cache each of these calls for some time
    // TODO - promise reuse this function
    const destination = await this.getDestinationEntity();

    this._allEntities = [].concat(
        destination,
        (await this.buildParkEntities()).map((x) => {
          return {
            ...x,
            _destinationId: destination._id,
          };
        }),
        (await this.buildAttractionEntities()).map((x) => {
          return {
            ...x,
            _destinationId: destination._id,
          };
        }),
        (await this.buildShowEntities()).map((x) => {
          return {
            ...x,
            _destinationId: destination._id,
          };
        }),
        (await this.buildRestaurantEntities()).map((x) => {
          return {
            ...x,
            _destinationId: destination._id,
          };
        }),
    );

    this._allEntitiesLastStash = +new Date();
    return this._allEntities;
  }

  /**
   * Get all park entities within this destination.
   */
  async getParkEntities() {
    const entities = await this.getAllEntities();
    return entities.filter((e) => e.entityType === entityType.park);
  }

  /**
   * Get all destination entities within this destination.
   */
  async getDestinationEntities() {
    const entities = await this.getAllEntities();
    return entities.filter((e) => e.entityType === entityType.destination);
  }

  /**
   * Get all attraction entities within this destination.
   */
  async getAttractionEntities() {
    const entities = await this.getAllEntities();
    return entities.filter((e) => e.entityType === entityType.attraction);
  }

  /**
   * Get all show entities within this destination.
   */
  async getShowEntities() {
    const entities = await this.getAllEntities();
    return entities.filter((e) => e.entityType === entityType.show);
  }

  /**
   * Get all restaurant entities within this destination.
   */
  async getRestaurantEntities() {
    const entities = await this.getAllEntities();
    return entities.filter((e) => e.entityType === entityType.restaurant);
  }

  /**
   * Given an internal entity ID, return it's full entity object
   * @param {string} id
   * @return {object}
   */
  async getEntityFromId(id) {
    const entities = await this.getAllEntities();
    return entities.find((x) => x._id === id);
  }

  // TODO - live data API methods in destination

  /**
   * Update an entity with livedata
   * @param {string} internalId
   * @param {object} data
   */
  async updateEntityLiveData(internalId, data) {
    // format incoming livedata to iron our any weird sorting inconsistency
    //  sort showtimes by startTime
    if (data?.showtimes) {
      data.showtimes.sort((a, b) => {
        if (!a.startTime || !b.startTime) return false;
        return moment(a.startTime).unix() - moment(b.startTime).unix();
      });
    }

    // stack up any emit events we want to send
    //  we will build these up inside our database transaction,
    //  but don't actually send them until we've released our lock
    const events = [];

    // get our entity doc
    const entity = await this.getEntityFromId(internalId);
    if (!entity) {
      return;

      this.emit('error', internalId, 'UNKNOWN_ENTITY_LIVEDATA', {
        message: `Trying to assign live data update to unknown entity ${internalId}`,
        data,
      });
      return;
    }

    // validate incoming data
    const validationErrors = getLiveDataErrors(data);
    if (validationErrors !== null) {
      this.emit('error', internalId, 'INVALID_LIVEDATA', {
        message: `Error validating incoming live data [${internalId}] ${JSON.stringify(data)}.
\t${validationErrors.map((x) => `${x.dataPath} ${x.message}`).join('\n\t')}`,
        data,
      });
      return;
    }

    // lock our live data in a transaction to avoid weird conflicts
    await this.cache.runTransaction(async (lock) => {
      try {
        // check live data hasn't changed from cache
        const storedHash = await this.getLiveHash(internalId, lock);

        if (storedHash) {
          const newHash = data ? objHash(data) : undefined;
          if (newHash === storedHash) {
            // incoming data matches stored data! no change
            return;
          }
        }

        // TODO - get previous data and diff
        // const existingData = await this.getLiveCache(internalId, lock);

        // TODO - identify specific live data type changes
        //  eg. broadcast "queueupdate" changes etc.

        // always push a general "liveupdate" event
        events.push(['liveupdate', internalId, data]);

        // store locally
        await this.setLiveCache(internalId, data, lock);
      } catch (e) {
        console.error(e);
      }
    });

    // emit all our events *outside* the database transaction
    //  so we don't block database IO any longer than we need to
    events.forEach((ev) => {
      this.emit(...ev);
    });
  }

  /**
   * Build all live data for entities - implement this function in child class
   */
  async buildEntityLiveData() {
    throw new Error('buildEntityLiveData() needs an implementation', this.constructor.name);
  }

  /**
   * Build all schedule data for entities - implement this function in child class
   * Returns array of schedule objects
   */
  async buildEntityScheduleData() {
    throw new Error('buildEntityScheduleData() needs an implementation', this.constructor.name);
  }

  /**
   * Get all live data for entities
   */
  async getEntityLiveData() {
    await this.init();
    const liveData = (await this.buildEntityLiveData()) || [];

    // process all live data we generated
    for (let liveDataIdx = 0; liveDataIdx < liveData.length; liveDataIdx++) {
      const data = liveData[liveDataIdx];
      try {
        await this.updateEntityLiveData(data._id, data);
      } catch (e) {
        // if (!e instanceof EntityNotFound) {
        console.error(`Failed to apply live data to ${data._id}`);
        console.error(e);
        // }
        // 19411262;entityType=Attraction = Pop/Art Skyliner Line
        // 19404062;entityType=Attraction = Hollywood Studios Skyliner Line
        // 19404065;entityType=Attraction = Epcot Skyliner Line
      }
    }

    return liveData;
  }

  /**
   * Get all schedules for this destination's entites
   * @return {Array<object>} Array of objects containing _id and schedules
   */
  async getEntitySchedules() {
    await this.init();
    const scheduleData = (await this.buildEntityScheduleData()) || [];

    // validate schedule data
    scheduleData.forEach((data, idx) => {
      const errors = validateEntitySchedule(data);
      if (errors) {
        this.emit('error', data._id, 'INVALID_SCHEDULEDATA', {
          message: `Destination returned invalid schedule data for ${data._id}`,
          data: errors,
        });
        // clear out invalid schedule data
        data.schedule = [];
      } else {
        // sort data by openingTime
        data.schedule.sort((a, b) => {
          return moment(a.openingTime).valueOf() - moment(b.openingTime).valueOf();
        });
      }
    });

    return scheduleData;
  }

  /**
   * Clear the cache for a given meta function
   * @private
   * @param {string} functionName
   * @param {Array<*>} args
   */
  async _clearFunctionCache(functionName, args = []) {
    let funcCacheName = `metacache_${functionName}`;
    if (args.length > 0) {
      funcCacheName = `metacache_${functionName}_${args.map((x) => JSON.stringify(x)).join(',')}`;
    }
    await this.cache.set(funcCacheName, null, -1);
  }

  /**
   * Helper function to call a function, passing in a date for each date x days in the future.
   * First date will be current park date.
   * @param {Function} func
   * @param {Number} dates
   */
  async forEachUpcomingDate(func, dates = 30) {
    const now = this.getTimeNowMoment();
    const end = now.clone().add(dates, 'days');

    const results = [];

    for (; now.isSameOrBefore(end, 'day'); now.add(1, 'day')) {
      const d = await func(now.clone());
      if (d) {
        results.push(d);
      }
    }

    return results;
  }

  /**
   * Get the app version of an Android package ID
   * Only returns a valid response for apps tracked in the appwatch tracker service
   * @param {string} packageId
   * @return {string|undefined}
   */
  async getAndroidAPPVersion(packageId) {
    // cache 12 hours
    '@cache|720';
    const resp = await this.http(
        'GET',
        `https://appwatch.themeparks.wiki/latest/${packageId}`,
        {},
    );
    return resp?.body?.version || undefined;
  }
}

export default Destination;

import {attractionType, statusType, queueType, tagType, scheduleType, entityType} from '../parkTypes.js';
import moment from 'moment-timezone';
import Destination from '../destination.js';

/**
 * Efteling Park Object
 */
export class Efteling extends Destination {
  /**
   * Create a new Efteling Park object
   * @param {object} options
   */
  constructor(options = {}) {
    options.name = options.name || 'Efteling';
    options.timezone = options.timezone || 'Europe/Amsterdam';

    options.apiKey = options.apiKey || '';
    options.apiVersion = options.apiVersion || '';
    options.appVersion = options.appVersion || '';

    options.searchUrl = options.searchUrl || 'https://prd-search-acs.efteling.com/2013-01-01/search';
    options.waitTimesUrl = options.waitTimesUrl || 'https://api.efteling.com/app/wis/';

    // bump cache to invalidate the POI data that has been updated
    options.cacheVersion = 1;

    super(options);

    if (!this.config.apiKey) throw new Error('Missing Efteling apiKey');
    if (!this.config.apiVersion) throw new Error('Missing Efteling apiVersion');
    if (!this.config.appVersion) throw new Error('Missing Efteling appVersion');

    this.http.injectForDomain({
      // match either of the API domains
      $or: [
        {
          hostname: 'api.efteling.com',
        },
        {
          hostname: 'prd-search-acs.efteling.com',
        },
      ],
    }, (method, url, data, options) => {
      // all requests from the app to any efteling subdomain should send these headers
      options.headers['x-app-version'] = this.config.appVersion;
      options.headers['x-app-name'] = 'Efteling';
      options.headers['x-app-id'] = 'nl.efteling.android';
      options.headers['x-app-platform'] = 'Android';
      options.headers['x-app-language'] = 'en';
      options.headers['x-app-timezone'] = this.config.timezone;
      // override user-agent here, rather than class-wide
      //  any other non-Efteling API requests can use the default user-agent
      options.headers['user-agent'] = 'okhttp/4.4.0';
      options.compressed = true;
    });

    this.http.injectForDomain({
      // only use these headers for the main API domain
      hostname: 'api.efteling.com',
    }, (method, url, data, options) => {
      // api.efteling.com requries an API key as well as the above headers
      options.headers['x-api-key'] = this.config.apiKey;
      options.headers['x-api-version'] = this.config.apiVersion;
    });
  }

  /**
   * Fetch POI data from Efteling API
   * @return {array<object>}
   */
  async _fetchPOIData({language = en} = {}) {
    // cache for 12 hours
    '@cache|720';

    // curl -H 'Host: prd-search-acs.efteling.com' -H 'user-agent: okhttp/4.4.0' -H 'x-app-version: v3.7.1' -H 'x-app-name: Efteling' -H 'x-app-id: nl.efteling.android' -H 'x-app-platform: Android' -H 'x-app-language: en' -H 'x-app-timezone: Europe/London' --compressed 'https://prd-search-acs.efteling.com/2013-01-01/search?q.parser=structured&size=1000&q=%28and%20%28phrase%20field%3Dlanguage%20%27en%27%29%29'
    const fetchedPOIData = await this.http('GET', this.config.searchUrl, {
      'size': 1000,
      'q.parser': 'structured',
      'q': `(and (phrase field=language \'${language}\'))`,
    });

    return fetchedPOIData?.body?.hits?.hit;
  }

  /**
   * Get Efteling POI data
   * This data contains general ride names, descriptions etc.
   * Wait time data references this to get ride names
   */
  async getPOIData() {
    '@cache|5';

    // grab English data first
    const data = await this._fetchPOIData({language: 'en'});
    if (!data) {
      throw new Error('Failed to fetch Efteling POI data [en]');
    }

    // also grab native language data and insert any missing entries
    const nativeData = await this._fetchPOIData({language: 'nl'});
    if (!nativeData) {
      throw new Error('Failed to fetch Efteling POI data [nl]');
    }

    // merge the two arrays with English data replacing NL data
    const mergedData = nativeData.map((nativeItem) => {
      const englishItem = data.find((item) => item.fields.id === nativeItem.fields.id);
      if (!englishItem) {
        // if the native item is missing, add it to the end of the array
        return nativeItem;
      }

      // if English version exists, use that one
      return englishItem;
    });

    const poiData = {};
    mergedData.forEach((hit) => {
      // skip any entries that aren't shown in the app
      if (hit.hide_in_app) return;

      if (hit.fields) {
        poiData[hit.fields.id] = {
          id: hit.fields.id,
          name: hit.fields.name,
          type: hit.fields.category,
          props: hit.fields.properties,
        };

        // hard-code station names so they can be distinct
        if (hit.fields.id === 'stoomtreinr') {
          poiData[hit.fields.id].name = poiData[hit.fields.id].name + ' - Oost';
        }
        if (hit.fields.id === 'stoomtreinm') {
          poiData[hit.fields.id].name = poiData[hit.fields.id].name + ' - Marerijk';
        }

        // try to parse lat/long
        //  edge-case: some rides have dud "0.0,0.0" location, ignore these
        if (hit.fields.latlon && hit.fields.latlon !== '0.0,0.0') {
          const match = /([0-9.]+),([0-9.]+)/.exec(hit.fields.latlon);
          if (match) {
            poiData[hit.fields.id].location = {
              latitude: Number(match[1]),
              longitude: Number(match[2]),
            };
          }
        }

        // check for any alternative versions of the ride
        //  this is usually the single rider line, though one is a "boatride"
        if (hit.fields.alternateid && hit.fields.alternatetype === 'singlerider') {
          poiData[hit.fields.id].singleRiderId = hit.fields.alternateid;
        }
      }
    });

    return poiData;
  }

  /**
   * Get calendar data for the given month and year
   * @param {string} month
   * @param {string} year
   * @return {array<object>}
   */
  async getCalendarMonth(month, year) {
    return await this.cache.wrap(`calendar_${year}_${month}`, async () => {
      const data = await this.http(
        'GET',
        `https://www.efteling.com/service/cached/getpoiinfo/en/${year}/${month}`,
        null,
        {
          headers: {
            'X-Requested-With': 'XMLHttpRequest',
            'referer': 'https://www.efteling.com/en/park/opening-hours?app=true',
            'cookie': 'website#lang=en',
          },
          json: true,
        },
      );

      // Efteling returns 400 once the month is in the past
      if (data.statusCode === 400) {
        return undefined;
      }

      if (!data?.body?.OpeningHours) throw new Error(`Unable to find opening hours for Efteling ${data.body}`);

      return data.body;
    }, 1000 * 60 * 60 * 12); // 12 hours
  }

  /**
 * Get restaurant operating hours from API
 * @param {string} day
 * @param {string} month
 * @param {string} year
 */
  async getRestaurantOperatingHours(day, month, year) {
    return await this.cache.wrap(`restaurant_${year}_${month}_${day}`, async () => {
      const waitTimes = await this.http('GET', this.config.waitTimesUrl, {
        language: 'en',
      });

      if (!waitTimes?.body?.AttractionInfo) {
        throw new Error(`Unable to find restaurant operating hours for Efteling ${data.body}`);
      }

      return waitTimes.body;
    }, 1000 * 60 * 60 * 12); // 12 hours
  }

  /**
   * Return restaurant operating hours for the supplied date
   * @param {moment} date
   */
  async _getRestaurantOperatingHoursForDate(date) {
    const cal = await this.getRestaurantOperatingHours(date.format('D'), date.format('M'), date.format('YYYY'));

    if (cal === undefined) return undefined;

    const data = cal.AttractionInfo;

    return data.map((entry) => {
      if (entry.Type !== 'Horeca') return;

      if (!entry.OpeningTimes || entry.OpeningTimes.length == 0) {
        return {
          restaurantID: entry.Id,
          openingTime: 0,
          closingTime: 0,
          status: statusType.closed,
        };
      }

      const openingTimes = entry.OpeningTimes;

      return {
        restaurantID: entry.Id,
        openingTime: moment(openingTimes[0].HourFrom).format(),
        closingTime: moment(openingTimes[0].HourTo).format(),
        type: scheduleType.operating,
      };
    }).filter((x) => x !== undefined);
  }

  /**
   * Helper function to build a basic entity document
   * Useful to avoid copy/pasting
   * @param {object} data 
   * @returns {object}
   */
  buildBaseEntityObject(data) {
    const entity = Destination.prototype.buildBaseEntityObject.call(this, data);

    entity._id = data?.id || entity._id;
    entity.name = data?.name || entity.name;

    // add location (if found)
    if (data?.location !== undefined) {
      entity.location = {
        longitude: data.location.longitude,
        latitude: data.location.latitude,
      };
    }

    // TODO - extra facet data
    /*
    // look for any other useful tags
    // may get wet
    await this.toggleAttractionTag(id, tagType.mayGetWet, p.props.indexOf('wet') >= 0);
    // tag "pregnant people should not ride" attractions
    await this.toggleAttractionTag(
      id,
      tagType.unsuitableForPregnantPeople,
      p.props.indexOf('pregnantwomen') >= 0,
    );

    // single rider queue available?
    await this.setAttractionTag(
      id,
      null,
      tagType.singleRider,
      !!p.singleRiderId,
    );

    // look for attraction minimum height
    const minHeightProp = p.props.find((prop) => prop.indexOf('minimum') === 0);
    if (minHeightProp !== undefined) {
      const minHeightNumber = Number(minHeightProp.slice(7));
      if (!isNaN(minHeightNumber)) {
        await this.setAttractionTag(id, 'minimumHeight', tagType.minimumHeight, {
          height: minHeightNumber,
          unit: 'cm',
        });
      }
    }*/

    return entity;
  }

  /**
   * Build the destination entity representing this destination
   */
  async buildDestinationEntity() {
    return {
      ...this.buildBaseEntityObject({
        name: "Efteling Themepark Resort",
      }),
      _id: 'eftelingresort',
      slug: 'eftelingresort',
      entityType: entityType.destination,
    };
  }

  /**
   * Build the park entities for this destination
   */
  async buildParkEntities() {
    const destination = await this.buildDestinationEntity();
    return [
      {
        ...this.buildBaseEntityObject({
          name: this.config.name,
        }),
        _id: 'efteling',
        _destinationId: destination._id,
        _parentId: destination._id,
        slug: 'efteling',
        entityType: entityType.park,
      },
    ];
  }

  async _buildArrayOfEntitiesOfType(type, fields = {}) {
    const destination = await this.buildDestinationEntity();
    const poi = await this.getPOIData();

    // some valid attraction types from the Efteling API:
    // 'attraction', 'show', 'merchandise', 'restaurant', 'fairytale', 'facilities-toilets', 'facilities-generic', 'eventlocation', 'game'

    const attrs = [];

    const poiKeys = Object.keys(poi);
    for (let i = 0; i < poiKeys.length; i++) {
      const id = poiKeys[i];
      const p = poi[id];

      // if poi data matches our wanted types
      if (p.type === type) {
        const attr = {
          ...fields,
          ...this.buildBaseEntityObject(p),
          _destinationId: destination._id,
          // TODO - are all rides/shows inside the park?
          _parkId: 'efteling',
          _parentId: 'efteling',
        };

        attrs.push(attr);
      }
    }

    return attrs;
  }

  /**
   * Build the attraction entities for this destination
   */
  async buildAttractionEntities() {
    return this._buildArrayOfEntitiesOfType('attraction', {
      entityType: entityType.attraction,
      attractionType: attractionType.ride,
    });
  }

  /**
   * Build the show entities for this destination
   */
  async buildShowEntities() {
    return this._buildArrayOfEntitiesOfType('show', {
      entityType: entityType.show,
    });
  }

  /**
   * Build the restaurant entities for this destination
   */
  async buildRestaurantEntities() {
    // TODO
    return [];
  }

  async _fetchWaitTimes() {
    // cache 1 minute
    '@cache|1';
    return (await this.http('GET', this.config.waitTimesUrl, {
      language: 'en',
    })).body;
  }

  /**
   * @inheritdoc
   */
  async buildEntityLiveData() {
    const poiData = await this.getPOIData();

    // this function should return all the live data for all entities in this destination
    const waitTimes = await this._fetchWaitTimes();

    const attractions = waitTimes?.AttractionInfo;
    if (!attractions) throw new Error('Efteling wait times response missing AttractionInfo');

    const livedata = [];

    // first, look for single-rider entries
    const singleRiderData = [];
    for (let i = 0; i < attractions.length; i++) {
      const entry = attractions[i];
      if (poiData[entry.Id] === undefined) {
        // if we don't have POI data for this attraction, check for single rider IDs and update the main attraction
        const singleRiderPOI = Object.keys(poiData).find((k) => {
          return poiData[k].singleRiderId && poiData[k].singleRiderId === entry.Id;
        });

        if (singleRiderPOI !== undefined) {
          // we have found a matching single-rider entry!
          singleRiderData.push({
            id: singleRiderPOI,
            time: parseInt(entry.WaitingTime, 10),
          });
        }
      }
    }

    for (let i = 0; i < attractions.length; i++) {
      const entry = attractions[i];
      if (entry.Type !== 'Attraction') continue;
      if (poiData[entry.Id] === undefined) continue;

      const live = {
        _id: entry.Id,
      };

      let rideStatus = null;
      const rideWaitTime = parseInt(entry.WaitingTime, 10);
      const rideState = entry.State.toLowerCase();
      // update ride with wait time data
      if (rideState === 'storing' || rideState === 'tijdelijkbuitenbedrijf') {
        // Ride down because of an interruption
        rideStatus = statusType.down;
      } else if (rideState === 'buitenbedrijf') {
        // ride is closed "for the day"
        rideStatus = statusType.closed;
      } else if (rideState === 'inonderhoud') {
        // Ride down because of maintenance/refurbishment
        rideStatus = statusType.refurbishment;
      } else if (rideState === 'gesloten' || rideState === '' || rideState === 'wachtrijgesloten' || rideState === 'nognietopen') {
        // ride is "closed"
        rideStatus = statusType.closed;
      } else if (rideState === 'open') {
        // Ride operating
        rideStatus = statusType.operating;
      }

      if (rideStatus === null) {
        this.emit('error', new Error(`Unknown Efteling rideStatus ${JSON.stringify(rideState)}`));
        console.log('Unknown Efteling rideStatus', JSON.stringify(rideState));
      }

      live.status = rideStatus;
      live.queue = {
        [queueType.standBy]: {
          waitTime: rideStatus == statusType.operating ? rideWaitTime : null,
        },
      };

      // add any single rider data (if available)
      const singleRider = singleRiderData.find((x) => x.id === entry.Id);
      if (singleRider) {
        live.queue[queueType.singleRider] = {
          waitTime: rideStatus == statusType.operating ? singleRider.time : null,
        };
      }

      livedata.push(live);
    }

    return livedata;
  }

  /**
   * Return schedule data for all scheduled entities in this destination
   * Eg. parks
   * @returns {array<object>}
   */
  async buildEntityScheduleData() {
    // get operating hours for next x months
    const parkSchedule = [];

    const now = this.getTimeNowMoment();
    const monthsToFetch = 3;
    const end = now.clone().add(monthsToFetch, 'months');
    for (; now.isSameOrBefore(end, 'month'); now.add(1, 'month')) {
      const calData = await this.getCalendarMonth(now.format('M'), now.format('YYYY'));
      if (calData === undefined) continue;

      calData.OpeningHours.forEach((x) => {
        const date = moment.tz(x.Date, 'YYYY-MM-DD', this.config.timezone);
        x.OpeningHours.sort((a, b) => a.Open - b.Open);
        x.OpeningHours.forEach((d, idx) => {
          const open = d.Open.split(':').map(Number);
          const close = d.Close.split(':').map(Number);
          parkSchedule.push({
            date: date.format('YYYY-MM-DD'),
            openingTime: date.clone().set('hour', open[0]).set('minute', open[1]).format(),
            closingTime: date.clone().set('hour', close[0]).set('minute', close[1]).format(),
            type: idx === 0 ? scheduleType.operating : scheduleType.informational,
            description: idx === 0 ? undefined : 'Evening Hours',
          });
        });
      });
    }

    return [
      {
        _id: 'efteling',
        schedule: parkSchedule,
      }
    ];
  }

}

export default Efteling;

import ConfigBase from '../configBase.js';
import moment from 'moment-timezone';
import zlib from 'zlib';
import util from 'util';
import HTTP from './http.js';

const zDecompress = util.promisify(zlib.unzip);
const zCompress = util.promisify(zlib.deflate);

/**
 * A super-class that Parks/Resorts/etc. inherit from.
 * Handles general logic for objects that are a place/entity.
 */
export class Entity extends ConfigBase {
  /**
   * Construct a new Entity
   * @param {object} options
   */
  constructor(options = {}) {
    // offline mode, never request any data, rely on manually serialised data to run
    options.offline = options.offline || false;

    // generate a random Android user-agent if we aren't supplied one
    options.useragent = options.useragent || null;

    super(options);

    if (!this.config.name) {
      throw new Error(`Missing name for constructed Entity object ${this.constructor.name}`);
    }

    if (!this.config.timezone) {
      throw new Error(`Missing timezone for constructed Entity object ${this.constructor.name}`);
    }
    if (moment.tz.names().indexOf(this.config.timezone) < 0) {
      throw new Error(`Entity object ${this.constructor.name} gives an invalid timezone: ${this.config.timezone}`);
    }

    this.http = new HTTP();
    if (this.config.useragent) {
      this.http.useragent = this.config.useragent;
    }

    // debug log all HTTP requests
    this.http.injectForDomain({hostname: {$exists: true}}, (method, url) => {
      this.log(method, url);
    });

    // offline function data
    this._offlineFunctions = [];
    this._offlineData = {};
    this._hasOfflineData = false;
    this._offlinePromise = null;
    this._offlinePromiseResolve = null;
  }

  /**
   * Debug log
   * @param  {...any} args Message to debug log
   */
  log(...args) {
    console.log(`[\x1b[32m${this.getUniqueID()}\x1b[0m]`, ...args);
  }

  /**
   * Get a globally unique ID for this entity
   * @return {string}
   */
  getUniqueID() {
    // by default, return the class name
    return this.constructor.name;
  }

  /**
   * Return the current time for this entity in its local timezone
   * @return {moment}
   */
  getTimeNowMoment() {
    return moment().tz(this.config.timezone);
  }

  /**
   * Return the current time for this entity in its local timezone
   * @return {string}
   */
  getTimeNow() {
    return this.getTimeNowMoment().format();
  }

  /**
   * Get entity's human-friendly name string
   * @return {string}
   */
  get name() {
    return this.config.name;
  }

  /**
   * Is this object operating offline?
   */
  get offline() {
    return !!this.config.offline;
  }

  /**
   * Register a function on this entity for offline access
   * @param {string} functionName
   */
  registerOfflineFunction(functionName) {
    if (this[functionName] && typeof this[functionName] === 'function') {
      if (this._offlineFunctions.indexOf(functionName) < 0) {
        this._offlineFunctions.push(functionName);

        // if we're in offline mode...
        if (this.offline) {
          // override function and restore from our data cache instead
          this[functionName] = async () => {
            await this.ensureHasOfflineData();

            if (this._offlineData[functionName] !== undefined) {
              return this._offlineData[functionName];
            }
            return undefined;
          };
        }
      }
    }
  }

  /**
   * Called after loading serialised offline data
   */
  async _postOfflineLoad() {}

  /**
   * Serialise this entity
   * @param {object} bundle Bundle to read/write from/to
   * @param {boolean} saving Whether we are saving or loading during this serialise operation
   * @param {object} [options]
   * @param {number} [options.version=1] Version of the seialised data
   * @param {boolean} [options.recursive=true] Recurse through attached entities?
   */
  async serialise(bundle = {}, saving = true, options = {
    version: 1,
    recursive: true,
  }) {
    if (saving) {
      // === Saving ===
      // default options
      bundle.version = options.version;
      bundle.ar = {
        functions: {},
        children: [],
      };

      // loop over all offline functions and store their data
      for (let i=0; i<this._offlineFunctions.length; i++) {
        const functionName = this._offlineFunctions[i];
        bundle.ar.functions[functionName] = await this[functionName]();
      }

      // TODO - loop over child entities, call serialise on them, then store their bundle.ar in ours
    } else {
      // === Loading ===
      // decompress/load the data
      const bundleBuffer = await zDecompress(bundle);
      bundle = JSON.parse(bundleBuffer.toString('utf8'));

      const version = bundle.version || 0;
      // check we understand this bundle version
      if (version !== 1) {
        throw new Error('Unable to load serialised bundle version', version);
      }

      // restore function data
      this._offlineData = bundle.ar.functions;

      // TODO - restore child entities
    }

    if (saving) {
      // pack and gz to buffer
      const bundleData = JSON.stringify(bundle);
      return await zCompress(bundleData);
    }

    // after loading, run any postUpdate functions
    this._postOfflineLoad();

    this._hasOfflineData = true;

    // check if any process if waiting for offline data to be ready
    if (this._offlinePromiseResolve !== null) {
      this._offlinePromiseResolve();
      this._offlinePromiseResolve = null;
      this._offlinePromise = null;
    }
  }

  /**
   * Await until offline data is present
   */
  async ensureHasOfflineData() {
    if (this.offline && !this._hasOfflineData) {
      if (this._offlinePromise === null) {
        this._offlinePromise = new Promise((resolve) => {
          this._offlinePromiseResolve = resolve;
        });
      }
      return this._offlinePromise;
    }
  }

  /**
   * Register a new injection for a specific domain
   * @param {object} filter Mongo-type query to use to match a URL
   * @param {function} func Function to call with needle request to inject extra data into.
   * Function will take arguments: (method, URL, data, options)
   */
  async injectForDomain(filter, func) {
    this.http.injectForDomain(filter, func);
  }
}

export default Entity;

import EuropaParkDB from './europaparkdb.js';
import {statusType, queueType, scheduleType, returnTimeState, entityType, attractionType} from '../parkTypes.js';
import moment from 'moment-timezone';
import Destination from '../destination.js';

export class EuropaPark extends Destination {
  constructor(options = {}) {
    options.name = options.name || "Europa-Park";
    options.timezone = options.timezone || 'Europe/Berlin';

    options.parks = [
      {
        id: 493,
        scope: 'europapark',
      },
      {
        id: 494,
        scope: 'rulantica',
      },
    ];

    super(options);

    if (!this.config.parks) throw new Error('Missing Europa Park Configs');
  }

  /**
   * Get our resort database
   */
  get db() {
    return EuropaParkDB.get();
  }

  static idToString(id) {
    return `europa_${id}`;
  }

  /**
   * @inheritdoc
   */
  buildBaseEntityObject(data) {
    const entity = Destination.prototype.buildBaseEntityObject.call(this, data);

    entity.name = data?.name;

    // add any entity locations
    if (data?.longitude && data?.latitude) {
      entity.location = {
        latitude: data.latitude,
        longitude: data.longitude,
      };
    } else if (data?._src?.longitude && data?._src?.latitude) {
      entity.location = {
        latitude: data._src.latitude,
        longitude: data._src.longitude,
      };
    }

    if (data?.id) {
      entity._id = `${data.id}`;
    }

    return entity;
  }

  /**
   * Build the destination entity representing this resort
   */
  async buildDestinationEntity() {
    return {
      ...this.buildBaseEntityObject({
        name: this.config.name,
      }),
      _id: 'europapark',
      slug: 'europa',
      entityType: entityType.destination,
    };
  }

  /**
   * Build the park entities for this resort
   */
  async buildParkEntities() {
    // find all our parks
    const poiData = await this.db.getParkData();

    const parks = poiData.filter((x) => {
      return x.type === 'park';
    });

    const destination = await this.buildDestinationEntity();

    return this.config.parks.map((parkConfig) => {
      const park = parks.find((x) => x.id === parkConfig.id);
      if (!park) return undefined;

      return {
        ...this.buildBaseEntityObject(park),
        _id: `park_${park.id}`,
        _destinationId: destination._id,
        _parentId: destination._id,
        entityType: entityType.park,
        slug: parkConfig.scope,
      };
    }).filter((x) => !!x);
  }

  /**
   * Build the attraction entities for this destination
   */
  async buildAttractionEntities() {
    const attr = [];
    const destination = await this.buildDestinationEntity();

    for (let i = 0; i < this.config.parks.length; i++) {
      const scope = this.config.parks[i].scope;
      const parkId = `park_${this.config.parks[i].id}`;

      // get attractions for each park scope
      const attractions = await this.db.getEntities({
        '_src.scopes': {
          $elemMatch: {
            $eq: scope,
          },
        },
        entityType: {
          $ne: 'SHOW',
        },
      });

      attractions.filter((x) => {
        // don't send + Pass entrances as separate entities
        if (x?._src?.name && x._src.name.indexOf('+ Pass entrance') > 0) {
          return false;
        }
        return true;
      }).forEach((a) => {
        // push all our attractions into a big array
        attr.push({
          ...this.buildBaseEntityObject(a),
          _parentId: parkId,
          _parkId: parkId,
          _destinationId: destination._id,
          entityType: entityType.attraction,
          // TODO - detect other types
          attractionType: attractionType.ride,
        });
      });
    }

    return attr;
  }

  /**
   * Build the show entities for this destination
   */
  async buildShowEntities() {
    const destination = await this.buildDestinationEntity();

    const attrs = await this.db.getEntities({
      entityType: 'SHOW',
    });

    const shows = [];

    const parkId = `park_${this.config.parks[0].id}`;
    attrs.forEach((a) => {
      shows.push({
        ...this.buildBaseEntityObject(a),
        _parentId: parkId,
        _parkId: parkId,
        _destinationId: destination._id,
        entityType: entityType.show,
      });
    });

    return shows;
  }

  /**
   * Build the restaurant entities for this destination
   */
  async buildRestaurantEntities() {
    // TODO
    return [];
  }

  /**
   * @inheritdoc
   */
  async buildEntityLiveData() {
    const attrs = await this.db.getEntities();

    const codeToEntityId = attrs.filter((x) => {
      return !!x._src.code;
    }).reduce((acc, x) => {
      if (x.id) {
        acc[x._src.code] = x.id;
      }
      return acc;
    }, {});

    const waits = await this.db.getWaitingTimes();
    const showtimes = await this.db.getShowTimes();

    const livedata = [];

    // first, extract out all the virtual queue data
    const vQueueData = [];
    for (let i = 0; i < waits.waitingtimes.length; i++) {
      const wait = waits.waitingtimes[i];

      const realRide = await this.db.findEntity({
        '_src.vQueue.code': {
          $eq: wait.code,
        },
      });

      if (realRide !== undefined) {
        // we found a "ride" that has a matching virtual queue
        //  so this is a "dummy" ride that we want to inject back into the actual ride entity
        let state = returnTimeState.available;
        switch (wait.time) {
          case 666:
            state = returnTimeState.temporarilyFull;
            break;
          case 777:
            state = returnTimeState.finished;
            break;
          case (wait.time <= 91):
            state = returnTimeState.available;
            break;
        }

        // store vqueue data so we can pull this when looping over the actual wait time data
        const entityId = codeToEntityId[realRide._src.code];
        if (entityId) {
          vQueueData.push({
            _id: `${entityId}`,
            _ignoreCode: wait.code,
            returnStart: wait.startAt,
            returnEnd: wait.endAt,
            state,
          });
        }
      }
    }

    const getLiveDataEntry = (id) => {
      // search livedata for existing entry with this ID
      const existing = livedata.find((x) => x._id === id);
      if (existing) {
        return existing;
      }

      // create a new entry
      const newEntry = {
        _id: id,
        status: statusType.operating,
      };
      livedata.push(newEntry);
      return newEntry;
    };

    // now actually loop over the wait data
    for (let i = 0; i < waits.waitingtimes.length; i++) {
      const wait = waits.waitingtimes[i];

      // first check if this is a virtual queue "dummy" entry, and ignore
      if (vQueueData.find((x) => x._ignoreCode === wait.code)) {
        continue;
      }

      // get entity Id from waittime code
      const entityId = codeToEntityId[wait.code];
      if (!entityId) continue;

      const live = getLiveDataEntry(entityId);

      // if time == 90, wait time is reported as 90+ in-app
      // time == 91, virtual queue is open
      // time == 999, down
      // time == 222, closed refurb
      // time == 333, closed
      // time == 444, closed becaue weather
      // time == 555, closed because ice
      // time == 666, virtual queue is "temporarily full"
      // time == 777, virtual queue is completely full
      switch (wait.time) {
        case 999:
        case 444: // weather
        case 555: // ice
          live.status = statusType.down;
          break;
        case 222:
          live.status = statusType.refurbishment;
          break;
        case 333:
          live.status = statusType.closed;
          break;
      }

      // stand-by wait time
      if (!live.queue) live.queue = {};
      live.queue[queueType.standBy] = {
        waitTime: wait.time <= 90 ? wait.time : null,
      };

      // look for any virtual queue data
      const vQ = vQueueData.find((x) => `${x._id}` === `${entityId}`);
      if (vQ) {
        live.queue[queueType.returnTime] = {
          returnStart: vQ.returnStart,
          returnEnd: vQ.returnEnd,
          state: vQ.state,
        };
      }
    }

    // showtimes
    showtimes.showtimes.forEach((show) => {
      const live = getLiveDataEntry(`shows_${show.showId}`);

      const showEnt = attrs.find((x) => x.id === `shows_${show.showId}`);
      if (!showEnt) {
        return;
      }

      live.showtimes = show.today.map((startTime) => {
        // startTime is a time string
        return {
          startTime: moment(startTime).tz(this.config.timezone).format(),
          endTime: moment(startTime).add(showEnt._src.duration || 0, 'minutes').tz(this.config.timezone).format(),
          type: 'Performance',
        };
      });

      // show status based on showtimes available
      if (live.showtimes.length === 0) {
        live.status = statusType.closed;
      } else {
        // if last show has ended, then mark as closed
        const lastTime = live.showtimes.reduce((acc, x) => {
          if (moment(x.endTime).isAfter(acc)) {
            return moment(x.endTime);
          }
          return acc;
        }, moment(live.showtimes[0].startTime));

        const now = this.getTimeNowMoment();
        if (now.isAfter(lastTime)) {
          live.status = statusType.closed;
        }
      }
    });

    return livedata;
  }

  async _getCalendarForPark(parkConfig) {
    const cal = await this.db.getCalendar();
    const parkTimes = cal.seasons.filter(
      // filter opening hours for actual opening times (ignore closed times)
      // filter by scopes including our park Id
      (x) => !x.closed && x.scopes.indexOf(parkConfig.scope) >= 0,
    );

    const times = [];

    const buildDateString = (inDate, date) => {
      return moment.tz(inDate, this.config.timezone).set({
        year: date.year(),
        month: date.month(),
        date: date.date(),
      }).format();
    };

    parkTimes.forEach((hoursRange) => {

      const start = moment(hoursRange.startAt);
      const end = moment(hoursRange.endAt);

      for (let date = start.clone(); date.isSameOrBefore(end, 'day'); date.add(1, 'day')) {

        times.push({
          date: date.format('YYYY-MM-DD'),
          openingTime: buildDateString(hoursRange.startAt, date),
          closingTime: buildDateString(hoursRange.endAt, date),
          type: scheduleType.operating,
        });

        // hotel extra hours
        if (hoursRange.hotelStartAt && hoursRange.hotelEndAt) {
          times.push({
            date: date.format('YYYY-MM-DD'),
            openingTime: buildDateString(hoursRange.hotelStartAt, date),
            closingTime: buildDateString(hoursRange.hotelEndAt, date),
            type: scheduleType.extraHours,
            description: 'Open To Hotel Guests',
          });
        }
      }
    });

    return {
      _id: `park_${parkConfig.id}`,
      schedule: times,
    };
  }

  /**
   * Return schedule data for all scheduled entities in this destination
   * Eg. parks
   * @returns {array<object>}
   */
  async buildEntityScheduleData() {
    const scheds = [];
    for (let i = 0; i < this.config.parks.length; i++) {
      scheds.push(await this._getCalendarForPark(this.config.parks[i]));
    }
    return scheds;
  }
}

export default EuropaPark;

import Database from '../database.js';
import crypto from 'crypto';
import {URL} from 'url';
import {tagType, attractionType, entityType} from '../parkTypes.js';
import Blowfish from 'egoroof-blowfish';

const poiEntityTypes = [
  'shows',
  'pois',
];

const entityTypeToAttractionType = {
  'shows': entityType.show,
  'pois': entityType.attraction,
};

const subtypesToAllow = {
  'pois': [
    'attraction',
  ],
};

/**
 * Europa Park Database Class
 */
export class DatabaseEuropaPark extends Database {
  /**
   * @inheritdoc
   * @param {object} options
   */
  constructor(options = {}) {
    options.fbAppId = '';
    options.fbApiKey = '';
    options.fbProjectId = '';
    options.apiBase = '';
    options.encKey = '';
    options.encIV = '';
    options.authURL = '';
    options.userKey = options.userKey || 'v3_live_android_exozet_api_username';
    options.passKey = options.passKey || 'v3_live_android_exozet_api_password';
    options.appVersion = options.appVersion || '10.1.0';

    options.configPrefixes = ['EUROPAPARK'].concat(options.configPrefixes || []);

    super(options);

    if (!this.config.fbApiKey) throw new Error('Missing Europa Park Firebase API Key');
    if (!this.config.fbAppId) throw new Error('Missing Europa Park Firebase App ID');
    if (!this.config.fbProjectId) throw new Error('Missing Europa Park Firebase Project ID');
    if (!this.config.apiBase) throw new Error('Missing Europa Park API Base');
    if (!this.config.encKey) throw new Error('Missing Europa Park Encryption Key');
    if (!this.config.encIV) throw new Error('Missing Europa Park Encryption IV');
    if (!this.config.authURL) throw new Error('Missing Europa Park Token URL');

    this.http.injectForDomain({
      hostname: new URL(this.config.authURL).hostname,
    }, async (method, url, data, options) => {
      options.headers['user-agent'] = `EuropaParkApp/${this.config.appVersion} (Android)`;
    });

    this.http.injectForDomain({
      hostname: new URL(this.config.apiBase).hostname,
    }, async (method, url, data, options) => {
      options.headers['user-agent'] = `EuropaParkApp/${this.config.appVersion} (Android)`;

      const jwtToken = await this.getToken();
      if (jwtToken === undefined) {
        // refetch Firebase settings and try again
        await this.cache.set('auth', undefined, -1);
        const jwtTokenRetry = await this.getToken();
        options.headers['jwtauthorization'] = `Bearer ${jwtTokenRetry}`;
      } else {
        options.headers['jwtauthorization'] = `Bearer ${jwtToken}`;
      }
    });

    this.http.injectForDomainResponse({
      hostname: new URL(this.config.apiBase).hostname,
    }, async (response) => {
      // if error code is unauthorised, clear out our JWT token
      if (response.statusCode === 401) {
        // wipe any existing token
        await this.cache.set('access_token', undefined, -1);
        // this will be regenerated next time injectForDomain is run
        return undefined;
      }

      return response;
    });

    this.bf = new Blowfish(this.config.encKey, Blowfish.MODE.CBC, Blowfish.PADDING.PKCS5);
    this.bf.setIv(this.config.encIV);
  }

  /**
   * Get or generate a Firebase device ID
   */
  async getFirebaseID() {
    return await this.cache.wrap('fid', async () => {
      try {
        const fidByteArray = crypto.randomBytes(17).toJSON().data;
        fidByteArray[0] = 0b01110000 + (fidByteArray[0] % 0b00010000);
        const b64String = Buffer.from(String.fromCharCode(...fidByteArray))
          .toString('base64')
          .replace(/\+/g, '-')
          .replace(/\//g, '_');
        const fid = b64String.substr(0, 22);
        return /^[cdef][\w-]{21}$/.test(fid) ? fid : '';
      } catch (e) {
        this.emit('error', e);
        console.log(e);
        return '';
      }
    }, 1000 * 60 * 60 * 24 * 8); // 8days
  }

  /**
   * Get Europa Park config keys
   */
  async getConfig() {
    return await this.cache.wrap('auth', async () => {
      const fid = await this.getFirebaseID();

      const resp = await this.http(
        'POST',
        `https://firebaseremoteconfig.googleapis.com/v1/projects/${this.config.fbProjectId}/namespaces/firebase:fetch`,
        {
          'appInstanceId': fid,
          'appId': this.config.fbAppId,
          'packageName': 'com.EuropaParkMackKG.EPGuide',
          'languageCode': 'en_GB',
        }, {
        headers: {
          'X-Goog-Api-Key': this.config.fbApiKey,
        },
      },
      );

      const decrypt = (str) => {
        return this.bf.decode(Buffer.from(str, 'base64'), Blowfish.TYPE.STRING);
      };

      const ret = {};
      Object.keys(resp.body.entries).forEach((key) => {
        ret[key] = decrypt(resp.body.entries[key]);
      });
      return ret;
    }, 1000 * 60 * 60 * 6); // 6 hours
  }

  /**
   * Get our JWT Token
   */
  async getToken() {
    let expiresIn = 1000 * 60 * 60 * 24; // default: 1 day
    return await this.cache.wrap('access_token', async () => {
      const config = await this.getConfig();
      const resp = await this.http(
        'POST',
        this.config.authURL,
        {
          client_id: config[this.config.userKey],
          client_secret: config[this.config.passKey],
          grant_type: 'client_credentials',
        },
        {
          json: true,
        },
      );

      if (!resp || !resp.body) {
        throw new Error('Failed to fetch credentials for Europa API');
      }

      expiresIn = resp.body.expires_in * 1000;
      const token = resp.body.access_token;
      return token;
    }, () => {
      return expiresIn;
    });
  }

  /**
   * Get static data for all park entities
   */
  async getParkData() {
    return await this.cache.wrap('poi', async () => {
      // get the last checksum we received
      const checksum = (await this.cache.get('poi_checksum')) || 0;
      const data = await this.http(
        'GET',
        `${this.config.apiBase}/api/v1/latest/en/live/${checksum}`,
        undefined,
        {
          json: true,
          ignoreErrors: true, // we want 404 errors
        },
      );

      if (data.body?.error?.code === 404 && checksum > 0) {
        // return old data, hasn't changed
        return await this.cache.get('poi_store');
      }

      if (!data.body.package) return undefined;

      // collapse sub fields into one array
      const entities = [];
      Object.keys(data.body.package.data).forEach((key) => {
        data.body.package.data[key].forEach((x) => {
          entities.push({
            ...x,
            entityType: key,
          });
        });
      });

      // store this data indefinitely, we'll only override it if the checksum changes
      await this.cache.set('poi_store', entities, Number.MAX_SAFE_INTEGER);
      await this.cache.set('poi_checksum', data.body.package.checksum);

      return entities;
    }, 1000 * 60 * 60 * 2); // check every 2 hours for updates
  }

  /**
   * @inheritdoc
   */
  async _init() {

  }

  /**
   * Get waiting time data from API
   */
  async getWaitingTimes() {
    return this.cache.wrap('waittingtimes', async () => {
      return (await this.http('GET', `${this.config.apiBase}/api/v1/waitingtimes`)).body;
    }, 1000 * 60);
  }

  /**
   * Get Europa Park calendar data
   */
  async getCalendar() {
    return this.cache.wrap('seasons', async () => {
      return (await this.http('GET', `${this.config.apiBase}/api/v1/seasons/en`)).body;
    }, 1000 * 60 * 60 * 6);
  }

  /**
   * Get Europa Park show times
   */
  async getShowTimes() {
    return this.cache.wrap('showtimes', async () => {
      // TODO - other languages? does this only include English performances?
      return (await this.http('GET', `${this.config.apiBase}/api/v1/showtimes/en/live`)).body;
    }, 1000 * 60 * 60 * 6);
  }

  /**
   * @inheritdoc
   */
  async _getEntities() {
    const poiData = await this.getParkData();

    const ret = poiData.map((poi) => {
      if (!poi.name) return undefined;

      // which types do we want to return?
      if (!poiEntityTypes.includes(poi.entityType)) return undefined;

      // filter by subtypes, if we have any
      const subTypes = subtypesToAllow[poi.entityType];
      if (subTypes) {
        if (!subTypes.includes(poi.type)) return undefined;
      }

      // TODO - ignore entities that are no longer valid
      //  if (poi.validTo !== null) 

      // "queueing" entries are pretend entities for virtual queues
      if (poi.queueing) return undefined;

      // ignore queue map pointers
      if (poi.name.indexOf('Queue - ') === 0) return undefined;

      delete poi.versions;

      // check for virtual queue
      const nameLower = poi.name.toLowerCase();
      const vQueueData = poiData.find((x) => {
        return x.queueing && x.name.toLowerCase().indexOf(nameLower) > 0;
      });
      // virtual queue waitingtimes data
      // code === vQueueData.code
      // time can only ever be between 0-90, anything >90 is a special code
      // if time == 90, wait time is reported as 90+ in-app
      // time == 91, virtual queue is open
      // time == 999, down
      // time == 222, closed refurb
      // time == 333, closed
      // time == 444, closed becaue weather
      // time == 555, closed because ice
      // time == 666, virtual queue is "temporarily full"
      // time == 777, virtual queue is completely full
      // startAt/endAt - current virtual queue window

      const tags = [];

      tags.push({
        key: 'location',
        type: tagType.location,
        value: {
          longitude: poi.longitude,
          latitude: poi.latitude,
        },
      });

      if (poi.minHeight) {
        tags.push({
          key: 'minimumHeight',
          type: tagType.minimumHeight,
          value: {
            unit: 'cm',
            height: poi.minHeight,
          },
        });
      }

      if (poi.maxHeight) {
        tags.push({
          key: 'maximumHeight',
          type: tagType.maximumHeight,
          value: {
            unit: 'cm',
            height: poi.maxHeight,
          },
        });
      }

      return {
        id: `${poi.entityType}_${poi.id}`,
        name: poi.name,
        type: entityTypeToAttractionType[poi.entityType] == entityType.attraction ? attractionType.ride : undefined,
        entityType: entityTypeToAttractionType[poi.entityType] || entityType.attraction,
        tags,
        _src: {
          ...poi,
          vQueue: vQueueData,
        },
      };
    }).filter((x) => x !== undefined);

    return ret;
  }
}

export default DatabaseEuropaPark;

import {Destination} from '../destination.js';
import {attractionType, statusType, queueType, tagType, scheduleType, entityType} from '../parkTypes.js';
import moment from 'moment-timezone';

export class HerschendDestination extends Destination {
  constructor(options = {}) {
    options.timezone = options.timezone || 'America/New_York';

    // we have multiple Herschend parks, so we need to set the resortId
    options.destinationId = options.destinationId || '';
    // slug to use for this destination
    options.destinationSlug = options.destinationSlug || '';

    // base URL for live data API
    options.apiBase = options.apiBase || '';

    // base URL for the CRM API
    options.crmBaseURL = options.crmBaseURL || '';
    // auth credentials for accessing the CRM API
    options.crmAuth = options.crmAuth || '';
    // guid for this destination's calendar and POI data
    options.crmGUID = options.crmGUID || '';
    // categories for POI data
    options.crmAttractionsId = options.crmAttractionsId || '';
    options.crmDiningId = options.crmDiningId || '';
    options.crmShowId = options.crmShowId || '';

    // allow configuring all herschend parks with the HERSCHEND prefix env var
    options.configPrefixes = options.configPrefixes || ['HERSCHEND'];

    options.cacheVersion = options.cacheVersion || '1';

    super(options);

    if (!this.config.destinationId) throw new Error('Missing destinationId');
    if (!this.config.destinationSlug) throw new Error('Missing destinationSlug');
    if (!this.config.apiBase) throw new Error('HerschendDestination requires an apiBase');
    if (!this.config.crmBaseURL) throw new Error('Missing crmBaseURL');
    if (!this.config.crmAuth) throw new Error('Missing crmAuth');
    if (!this.config.crmGUID) throw new Error('Missing crmGUID');
    if (!this.config.crmAttractionsId) throw new Error('Missing crmAttractionsId');
    if (!this.config.crmDiningId) throw new Error('Missing crmDiningId');
    if (!this.config.crmShowId) throw new Error('Missing crmShowId');

    this.config.parkId = this.config.parkId || `${this.config.destinationSlug}park`;

    // inject into CRM API requests
    const crmURLHostname = new URL(this.config.crmBaseURL).hostname;
    this.http.injectForDomain({
      hostname: crmURLHostname,
    }, async (method, url, data, options) => {
      // add our auth credentials to the request
      options.headers = {
        ...options.headers,
        authorization: `Basic ${this.config.crmAuth}`,
      };

      options.json = true;
    });
  }

  /**
   * Get raw wait times data
   */
  async getWaitTimes() {
    '@cache|1'; // cache for 1 minute
    const resp = await this.http(`${this.config.apiBase}waitTimes/${this.config.destinationId}`);
    return resp.body;
  }

  /**
   * Get raw food wait times data
   */
  async getFoodWaitTimes() {
    '@cache|1'; // cache for 1 minute
    const resp = await this.http(`${this.config.apiBase}foodServiceWaitTimes/${this.config.destinationId}`);
    return resp.body;
  }

  /**
   * Get raw train times data
   */
  async getTrainTimes() {
    '@cache|1'; // cache for 1 minute
    const resp = await this.http(`${this.config.apiBase}trainServiceSchedules/${this.config.destinationId}`);
    return resp.body;
  }

  /**
   * Get raw attraction POI data
   */
  async getPOIData() {
    // cache for 12 hours
    '@cache|720';
    const resp = await this.http(`${this.config.crmBaseURL}api/park/activitiesbypark/${this.config.crmGUID}`);
    return resp.body;
  }

  /**
   * Helper function to build a basic entity document
   * Useful to avoid copy/pasting
   * @param {object} data 
   * @returns {object}
   */
  buildBaseEntityObject(data) {
    const entity = Destination.prototype.buildBaseEntityObject.call(this, data);

    if (data) {
      entity._tags = [];

      if (data.id) {
        entity._id = data.id;
      }

      // geo location
      if (data.mapLocation && data.mapLocation.latitude && data.mapLocation.longitude) {
        if (!entity.location) entity.location = {};
        entity.location.longitude = Number(data.mapLocation.longitude);
        entity.location.latitude = Number(data.mapLocation.latitude);
      }

      if (data.longitudeForDirections) {
        if (!entity.location) entity.location = {};
        entity.location.longitude = Number(data.longitudeForDirections);
      }
      if (data.latitudeForDirections) {
        if (!entity.location) entity.location = {};
        entity.location.latitude = Number(data.latitudeForDirections);
      }

      // entity name
      if (data.title) {
        entity.name = data.title;
      }

      if (data.heightRequirement && data.heightRequirement.minHeight > 0) {
        // convert from inches to cm
        const minHeightInCm = Math.floor(Number(data.heightRequirement.minHeight) * 2.54);

        entity._tags.push({
          id: 'minimumHeight',
          value: minHeightInCm,
        });
      }
    }

    return entity;
  }

  /**
   * Build the destination entity representing this destination
   */
  async buildDestinationEntity() {
    const doc = {};
    return {
      ...this.buildBaseEntityObject(doc),
      _id: this.config.destinationSlug,
      slug: this.config.destinationSlug,
      name: this.config.name,
      entityType: entityType.destination,
    };
  }

  /**
   * Build the park entities for this destination
   */
  async buildParkEntities() {
    return [
      {
        ...this.buildBaseEntityObject(null),
        _id: this.config.parkId,
        _destinationId: this.config.destinationSlug,
        _parentId: this.config.destinationSlug,
        name: this.config.name,
        slug: this.config.parkId,
        entityType: entityType.park,
      }
    ];
  }

  /**
   * Helper function to filter POI data for a chosen category GUID
   * @param {string} category 
   * @returns 
   */
  async getPOIForCategory(category, filterSeasonal = true) {
    const poiData = await this.getPOIData();

    const now = this.getTimeNowMoment();

    return poiData.activities.filter((x) => {
      if (x.activityListId !== category) return false;

      // check seasonality
      if (filterSeasonal) {
        if (x.seasonalStartDate) {
          const startDate = moment(x.seasonalStartDate);
          if (startDate.isAfter(now)) return false;
        }
        if (x.seasonalEndDate) {
          const endDate = moment(x.seasonalEndDate);
          if (endDate.isBefore(now)) return false;
        }
      }

      return true;
    });
  }

  /**
   * Build the attraction entities for this destination
   */
  async buildAttractionEntities() {
    const attrs = await this.getPOIForCategory(this.config.crmAttractionsId, false);

    return attrs.map((x) => {
      return {
        ...this.buildBaseEntityObject(x),
        entityType: entityType.attraction,
        attractionType: attractionType.ride,
        _destinationId: this.config.destinationSlug,
        _parkId: this.config.parkId,
        _parentId: this.config.parkId,
      };
    });
  }

  /**
   * Build the show entities for this destination
   */
  async buildShowEntities() {
    return [];

    const attrs = await this.getPOIForCategory(this.config.crmShowId, true);

    return attrs.map((x) => {
      return {
        ...this.buildBaseEntityObject(x),
        entityType: entityType.show,
        _destinationId: this.config.destinationSlug,
        _parkId: this.config.parkId,
        _parentId: this.config.parkId,
      };
    });
  }

  /**
   * Build the restaurant entities for this destination
   */
  async buildRestaurantEntities() {
    const attrs = await this.getPOIForCategory(this.config.crmDiningId, false);

    return attrs.map((x) => {
      return {
        ...this.buildBaseEntityObject(x),
        entityType: entityType.restaurant,
        _destinationId: this.config.destinationSlug,
        _parkId: this.config.parkId,
        _parentId: this.config.parkId,
      };
    });
  }

  /**
   * @inheritdoc
   */
  async buildEntityLiveData() {
    // get all the data we need
    const liveData = await this.getWaitTimes();
    const poiData = await this.getPOIData();

    return liveData.map((x) => {
      // find matching poiData for this live data
      const rideId = `${x.rideId}`;
      const poi = poiData.activities.find((y) => `${y.rideWaitTimeRideId}` === rideId);
      if (!poi) return null;

      const data = {
        _id: poi.id,
        status: statusType.closed,
        queue: {},
      };

      if (x.operationStatus === 'CLOSED' || x.operationStatus === 'UNKNOWN') {
        data.status = statusType.closed;
      } else if (x.operationStatus === 'TEMPORARILY CLOSED') {
        data.status = statusType.down;
      } else if (x.waitTimeDisplay.includes('UNDER')) {
        data.status = statusType.operating;
        // Wait time is not defined if text says "Under x minutes" - we'll set the ride time to x
        try {
          data.queue[queueType.standBy] = {
            waitTime: parseInt(x.waitTimeDisplay.split(' ')[1], 10),
          };
        } catch (e) {
          // fallback if formatting fails
          data.queue[queueType.standBy] = {
            waitTime: parseInt(x.waitTime, 10) || 0,
          };
        }
      } else if (x.waitTime) {
        data.status = statusType.operating;
        data.queue[queueType.standBy] = {
          waitTime: parseInt(x.waitTime, 10) || 0,
        };
      }

      return data;
    }).filter((x) => !!x);
  }

  /**
   * Fetch startdate and the following 4 days (total 5 days) of schedule data
   * @param {string} startDate 
   * @returns 
   */
  async fetchFiveDates(startDate) {
    // cache for 12 hours
    '@cache|720';
    const resp = await this.http(`${this.config.crmBaseURL}api/park/dailyschedulebytime?parkids=${this.config.crmGUID}&date=${startDate}&days=5`);
    return resp.body;
  }

  async fetchScheduleData(days = 90) {
    // cache for 12 hours
    '@cache|720';
    const now = this.getTimeNowMoment();
    const endDate = now.clone().add(days, 'days');
    const datesToFetch = [];
    while (now.isSameOrBefore(endDate, 'day')) {
      datesToFetch.push(now.format('YYYY-MM-DD'));
      now.add(5, 'day');
    }

    // fetch each date block
    const data = [];
    for (let i = 0; i < datesToFetch.length; i++) {
      const resp = await this.fetchFiveDates(datesToFetch[i]);
      data.push(...resp);
    }

    return data;
  }

  /**
   * Return schedule data for all scheduled entities in this destination
   * Eg. parks
   * @returns {array<object>}
   */
  async buildEntityScheduleData() {
    const parkHours = await this.fetchScheduleData();

    return [
      {
        _id: this.config.parkId,
        schedule: parkHours.map((x) => {
          // step 1: extract park hours from schedule data
          if (x?.parkHours) {
            return x.parkHours.find((y) => {
              // step 2: filter out park hours that don't match our park ID
              return y.cmsKey === this.config.crmGUID;
            });
          }
          return null;
        }).filter((x) => {
          // step 3: filter out nulls
          return !!x && !!x?.from && !!x?.to;
        }).map((x) => {
          // step 4: convert to our format
          const from = moment.tz(x.from, this.config.timezone);
          const to = moment.tz(x.to, this.config.timezone);
          return {
            date: from.format('YYYY-MM-DD'),
            type: "OPERATING",
            openingTime: from.format(),
            closingTime: to.format(),
          };
        }),
      }
    ];
  }
}

