import {Destination} from '../destination.js';
import {attractionType, statusType, queueType, tagType, scheduleType, entityType} from '../parkTypes.js';
import moment from 'moment';

export class Hersheypark extends Destination {
  constructor(options = {}) {
    options.timezone = options.timezone || 'America/New_York';

    options.apiKey = options.apiKey || '';
    options.baseURL = options.baseURL || 'https://hpapp.hersheypa.com';

    super(options);

    if (!this.config.apiKey) throw new Error('Missing apiKey');

    // hook into the base API URL to add our api key
    const baseURLHostname = new URL(this.config.baseURL).hostname;
    this.http.injectForDomain({
      hostname: baseURLHostname,
    }, async (method, url, data, options) => {
      options.headers = {
        ...options.headers,
        'x-api-key': this.config.apiKey,
      };
    });
  }

  /**
   * Fetch the POI data for this destination
   * @returns 
   */
  async fetchPOIData() {
    // cache for 1 day
    '@cache|1d';
    // fetch the park data from the API
    const response = await this.http.get(`${this.config.baseURL}/v2/index`);
    // return the park data
    return response.body;
  }

  /**
   * Fetch the live data for all entities in this destination
   */
  async fetchStatusData() {
    '@cache|2m'; // cache for 2 minutes
    const response = await this.http.get(`${this.config.baseURL}/v2/status`);
    return response.body;
  }

  /**
   * Get the main park POI data
   */
  async getParkPOIData() {
    '@cache|6h'; // cache for 6 hours
    const poi = await this.fetchPOIData();

    const parkData = poi.explore.find((x) => x.isHersheyPark);
    if (!parkData) {
      throw new Error('Missing park data');
    }

    return parkData;
  }

  /**
   * Helper function to build a basic entity document
   * Useful to avoid copy/pasting
   * @param {object} data 
   * @returns {object}
   */
  buildBaseEntityObject(data) {
    const entity = Destination.prototype.buildBaseEntityObject.call(this, data);

    if (data) {
      entity.name = data.name || undefined;

      if (data.latitude && data.longitude) {
        entity.location = {
          latitude: data.latitude,
          longitude: data.longitude,
        };
      }
    }

    return entity;
  }

  /**
   * Build the destination entity representing this destination
   */
  async buildDestinationEntity() {
    return {
      ...this.buildBaseEntityObject(null),
      _id: 'hersheypark',
      slug: 'hersheypark',
      name: 'Hersheypark',
      entityType: entityType.destination,
    };
  }

  /**
   * Build the park entities for this destination
   */
  async buildParkEntities() {
    const parkData = await this.getParkPOIData();

    return [
      {
        ...this.buildBaseEntityObject(parkData),
        _id: 'hersheyparkthemepark',
        _destinationId: 'hersheypark',
        _parentId: 'hersheypark',
        slug: 'hersheyparkthemepark',
        entityType: entityType.park,
      }
    ];
  }

  /**
   * Build the attraction entities for this destination
   */
  async buildAttractionEntities() {
    const poi = await this.fetchPOIData();
    const entities = [];
    for(const ride of poi.rides) {
      entities.push({
        ...this.buildBaseEntityObject(ride),
        _id: `rides_${ride.id}`,
        _destinationId: 'hersheypark',
        _parentId: 'hersheyparkthemepark',
        _parkId: 'hersheyparkthemepark',
        entityType: entityType.attraction,
        attractionType: attractionType.ride,
      });
    }
    return entities;
  }

  /**
   * Build the show entities for this destination
   */
  async buildShowEntities() {
    return [];
  }

  /**
   * Build the restaurant entities for this destination
   */
  async buildRestaurantEntities() {
    return [];
  }

  /**
   * @inheritdoc
   */
  async buildEntityLiveData() {
    const liveData = await this.fetchStatusData();

    return liveData.map((x) => {
      // only support live data for rides, update this to add support for other entities
      if (x.type !== 'rides') {
        return null;
      }

      const data = {
        _id: `${x.type}_${x.id}`,
        status: statusType.closed,
      };

      // statuses:
      //  3: park closed
      //  2: <unknown> (assumed down)
      //  1: open

      if (x.status === 2) {
        data.status = statusType.down;
      } else if (x.status === 1) {
        data.status = statusType.operating;

        if (data.wait !== null && data.wait !== undefined) {
          data.queue = {
            [queueType.standBy]: {
              waitTime: data.wait,
            },
          };
        }
      }

      return data;
    }).filter((x) => !!x);
  }

  /**
   * Return schedule data for all scheduled entities in this destination
   * Eg. parks
   * @returns {array<object>}
   */
  async buildEntityScheduleData() {
    // find park ID so we can grab schedule from exploreHours
    const parkData = await this.getParkPOIData();
    const parkId = parkData.id;

    const allPOIData = await this.fetchPOIData();

    const schedule = [];
    for(const date in allPOIData.exploreHours) {
      const hours = allPOIData.exploreHours[date];
      const parkHours = hours[parkId];
      if (!parkHours) {
        continue;
      }

      // parkHours is a string in the format: "10:00 AM - 10:00 PM"
      const [start, end] = parkHours.split(' - ');
      if (!start || !end) {
        continue;
      }

      const startTime = moment.tz(`${date} ${start}`, 'YYYY-MM-DD h:mma', this.config.timezone);
      const endTime = moment.tz(`${date} ${end}`, 'YYYY-MM-DD h:mma', this.config.timezone);

      schedule.push({
        date: date,
        openingTime: startTime.format(),
        closingTime: endTime.format(),
        type: 'OPERATING',
      });
    }

    return [
      {
        _id: 'hersheyparkthemepark',
        schedule,
      }
    ];
  }
}

import HarWriter from '../har.js';
import randomUseragent from 'random-useragent';
import moment from 'moment-timezone';
import sift from 'sift';
import needle from 'needle';
import promiseRetry from 'promise-retry';

let overrideFunc = null;
/**
 * Set an override function to use for HTTP requests
 * Accepts (method, URL, data, options)
 * @param {function} fn
 */
export function setOverrideFunction(fn) {
  overrideFunc = fn;
}

/**
 * Generate a random Android user agent for making network requests
 * @return {string}
 */
export function generateRandomAndroidUseragent() {
  return randomUseragent.getRandom((ua) => {
    return (ua.osName === 'Android');
  });
}

// start our har writer (if debugging)
const harWriter = process.env['THEMEPARKS_HAR'] ?
  new HarWriter({filename: `${process.env['THEMEPARKS_HAR']}.har`}) :
  null;

/**
   * Write a HTTP response to HAR file for debugging
   * @param {*} method
   * @param {*} url
   * @param {*} data
   * @param {*} options
   * @param {*} resp
   * @param {*} startTime
   * @param {*} timeTaken
   * @private
   */
async function writeToHAR(method, url, data, options, resp, startTime, timeTaken) {
  const objToArr = (obj) => {
    return Object.keys(obj).map((header) => {
      return {name: header, value: obj[header].toString()};
    });
  };

  const entry = {
    startedDateTime: startTime,
    time: timeTaken,
    request: {
      method: method,
      url: url,
      httpVersion: `HTTP/${resp.httpVersion}`, // this is actually the response, TODO
      cookies: [],
      headers: objToArr(options.headers), // not the actual headers needle sends - TODO, how to get these?
      queryString: method === 'GET' ? objToArr(data) : [], // TODO - parse from needle's .path
      postData: {
        mimeType: options.json ? 'application/json' : (options.headers['content-type'] || ''),
        params: method !== 'GET' ? [] : [],
        text: '',
      },
      headersSize: -1,
      bodySize: -1,
    },
    response: {
      status: resp.statusCode,
      statusText: resp.statusMessage,
      httpVersion: `HTTP/${resp.httpVersion}`,
      cookies: [],
      headers: objToArr(resp.headers),
      content: {
        size: resp.raw.length || -1,
        mimeType: resp.headers['content-type'],
        text: resp.raw.toString('base64'),
        encoding: 'base64',
      },
      redirectURL: '',
      headersSize: -1,
      bodySize: -1,
    },
    cache: {},
    timings: {
      send: -1,
      wait: -1,
      receive: -1,
    },
  };
  await harWriter.recordEntry(entry);
}

/**
 * HTTP helper with injection
 * @return {*}
 */
export const HTTP = (function() {
  this._httpInjections = [];
  this._httpResponseInjections = [];
  this.useragent = generateRandomAndroidUseragent();

  /**
   * Helper function to make an HTTP request for this park
   * Parks can automatically add in authentication headers etc. to requests sent to this function
   * @param {string} method HTTP method to use (GET,POST,DELETE, etc)
   * @param {string} url URL to request
   * @param {object} [data] data to send. Will become querystring for GET, body for POST
   * @param {object} [options = {}] Object containing needle-compatible HTTP options
   */
  const mainFunction = async (method, url, data, options = {}) => {
    // default to GET if we only have one argument
    if (url === undefined && data === undefined) {
      url = method;
      method = 'GET';
    }

    // always have a headers array
    if (!options.headers) {
      options.headers = {};
    }

    // default to accepting compressed data
    options.compressed = options.compressed === undefined ? true : options.compressed;

    // 10 seconds default timeout opening response
    options.response_timeout = options.response_timeout || 10000;
    // 30 seconds default timeout for reading data (for large data streams)
    options.read_timeout = options.read_timeout || 30000;

    // inject custom standard user agent (if we have one)
    //  do this before any custom injections so parks can optionally override this for each domain
    if (this.useragent && !options.headers['user-agent']) {
      options.headers['user-agent'] = this.useragent;
    }

    if (!options.headers['user-agent'] && process.env.DEFAULT_USER_AGENT) {
      // if no user-agent supplied, set a default one from the env
      options.headers['user-agent'] = process.env.DEFAULT_USER_AGENT;
    }

    // check any hostname injections we have setup
    const urlObj = new URL(url);
    const urlFilter = {
      protocol: urlObj.protocol,
      host: urlObj.host,
      hostname: urlObj.hostname,
      pathname: urlObj.pathname,
      search: urlObj.search,
      hash: urlObj.hash,
    };

    // wrap our needle call in a retry
    return await promiseRetry({
      retries: options.retries === undefined ? 3 : options.retries,
    }, async (retryFn) => {
      // make sure we run initial injections on each retry
      for (let injectionIDX = 0; injectionIDX < this._httpInjections.length; injectionIDX++) {
        const injection = this._httpInjections[injectionIDX];

        // check if the domain matches
        if (injection.filter(urlFilter)) {
          const injectionResp = await injection.func(method, url, data, options);
          if (injectionResp) {
            url = injectionResp.url || url;
            method = injectionResp.method || method;
            data = injectionResp.data || data;
            options = injectionResp.options || options;
          }
        }
      }

      // record some stats for the optional HAR Writer
      const startMs = +new Date();
      const startTime = moment(startMs).toISOString();

      // optionally override the HTTP function to use
      const httpFunc = overrideFunc ? overrideFunc : needle;

      return httpFunc(method, url, data, options).then(async (resp) => {
        // intercept response to write to our .har file
        if (harWriter) {
          await writeToHAR(method, url, data, options, resp, startTime, (+new Date()) - startMs);
        }

        // call any response injections
        for (let injectionIDX = 0; injectionIDX < this._httpResponseInjections.length; injectionIDX++) {
          const injection = this._httpResponseInjections[injectionIDX];

          // check if the domain matches
          // (reuse urlFilter from the incoming injections)
          if (injection.filter(urlFilter)) {
            resp = await injection.func(resp);
          }
        }

        // if our response if now undefined, retry our request
        if (resp === undefined) {
          return retryFn();
        }

        // if we got an error code, retry our request
        if (!options.ignoreErrors && resp.statusCode >= 400) {
          return retryFn();
        }

        // force response to JSON object if options.json is set
        if (options.json && resp.body && typeof resp.body === 'string') {
          try {
            resp.body = JSON.parse(resp.body);
          } catch (e) {
            // ignore
          }
        }

        return resp;
      });
    });
  };

  /**
   * Register a new injection for a specific domain
   * @param {object} filter Mongo-type query to use to match a URL
   * @param {function} func Function to call with needle request to inject extra data into.
   * Function will take arguments: (method, URL, data, options)
   */
  mainFunction.injectForDomain = (filter, func) => {
    // add to our array of injections, this is processing by HTTP()
    this._httpInjections.push({
      filter: sift(filter),
      func,
    });
  };

  /**
   * Register a new response injection for a specific domain
   * @param {object} filter Mongo-type query to use to match a URL
   * @param {function} func Function to call with needle response object to make changes
   * Function will take arguments: (response)
   * Function *must* return the response object back, or undefined if you want to force a retry
   */
  mainFunction.injectForDomainResponse = (filter, func) => {
    this._httpResponseInjections.push({
      filter: sift(filter),
      func,
    });
  };

  /**
   * Helper function to make a GET request
   */
  mainFunction.get = (url, data, options) => {
    return mainFunction('GET', url, data, options);
  };

  /**
   * Helper function to make a POST request
   */
  mainFunction.post = (url, data, options) => {
    return mainFunction('POST', url, data, options);
  };

  /**
   * Helper function to make a PUT request
   */
  mainFunction.put = (url, data, options) => {
    return mainFunction('PUT', url, data, options);
  };

  /**
   * Helper function to make a DELETE request
   */
  mainFunction.delete = (url, data, options) => {
    return mainFunction('DELETE', url, data, options);
  };

  /**
   * Helper function to make a PATCH request
   */
  mainFunction.patch = (url, data, options) => {
    return mainFunction('PATCH', url, data, options);
  };

  /**
   * Helper function to make a HEAD request
   */
  mainFunction.head = (url, data, options) => {
    return mainFunction('HEAD', url, data, options);
  };

  /**
   * Helper function to make a OPTIONS request
   */
  mainFunction.options = (url, data, options) => {
    return mainFunction('OPTIONS', url, data, options);
  };

  /**
   * Helper function to make a TRACE request
   */
  mainFunction.trace = (url, data, options) => {
    return mainFunction('TRACE', url, data, options);
  };

  return mainFunction;
});


export default HTTP;

import {Destination} from '../destination.js';
import {attractionType, statusType, queueType, tagType, scheduleType, entityType} from '../parkTypes.js';
import moment from 'moment-timezone';

export class Liseberg extends Destination {
  constructor(options = {}) {
    options.timezone = options.timezone || 'Europe/Stockholm';
    options.resortId = options.resortId || 'liseberg';

    options.baseURL = options.baseURL || '';

    super(options);

    if (!this.config.resortId) throw new Error('Missing resortId');
    if (!this.config.baseURL) throw new Error('Missing baseURL');
  }

  /**
   * Helper function to build a basic entity document
   * Useful to avoid copy/pasting
   * @param {object} data 
   * @returns {object}
   */
  buildBaseEntityObject(data) {
    const entity = Destination.prototype.buildBaseEntityObject.call(this, data);

    if (data) {
      if (data.id) {
        entity._id = `${data.id}`;
      }

      if (data.title) {
        entity.name = data.title;
      }

      if (data.coordinates && data.coordinates.longitude && data.coordinates.latitude) {
        entity.location = {
          longitude: data.coordinates.longitude,
          latitude: data.coordinates.latitude,
        };
      }
    }

    return entity;
  }

  /**
   * Build the destination entity representing this destination
   */
  async buildDestinationEntity() {
    return {
      ...this.buildBaseEntityObject(null),
      _id: 'liseberg',
      slug: 'liseberg',
      name: 'Liseberg',
      entityType: entityType.destination,
    };
  }

  /**
   * Build the park entities for this destination
   */
  async buildParkEntities() {
    return [
      {
        ...this.buildBaseEntityObject(null),
        _id: 'lisebergpark',
        _destinationId: 'liseberg',
        _parentId: 'liseberg',
        slug: 'lisebergpark',
        name: 'Liseberg',
        entityType: entityType.park,
      }
    ];
  }

  /**
   * Fetch the attraction states from Liseberg API
   */
  async fetchAttractionStates() {
    '@cache|1';
    const resp = await this.http('GET', `${this.config.baseURL}app/attractions/`);
    return resp.body;
  }

  /**
   * Build the attraction entities for this destination
   */
  async buildAttractionEntities() {
    const attractions = await this.fetchAttractionStates();

    return attractions.filter((x) => {
      return x?.type === 'attraction';
    }).map((attraction) => {
      return {
        ...this.buildBaseEntityObject(attraction),
        entityType: entityType.attraction,
        attractionType: attractionType.ride,
        _destinationId: 'liseberg',
        _parentId: 'lisebergpark',
        _parkId: 'lisebergpark',
      };
    });
  }

  /**
   * Build the show entities for this destination
   */
  async buildShowEntities() {
    return [];
  }

  /**
   * Build the restaurant entities for this destination
   */
  async buildRestaurantEntities() {
    return [];
  }

  /**
   * @inheritdoc
   */
  async buildEntityLiveData() {
    const attractions = await this.fetchAttractionStates();

    return attractions.map((attraction) => {
      const liveEntry = {
        _id: `${attraction.id}`,
      };

      if (attraction.state) {
        liveEntry.status = attraction.state.isOpen ? statusType.operating : statusType.closed;
        liveEntry.queue = {
          [queueType.standBy]: {
            waitTime: attraction.state.maxWaitTime,
          },
        };
      }

      return liveEntry;
    });
  }

  /**
   * Fetch the calendar for a given date (and x days further)
   * @param {string} dateString Date in YYYY-MM-DD format 
   * @param {number} [datesToFetch=30] Number of dates to fetch from the dateString date 
   * @returns 
   */
  async fetchCalendarForDate(dateString, datesToFetch = 7) {
    '@cache|720'; // cache for 12 hours
    try {
      const resp = await this.http('GET', `${this.config.baseURL}calendar/${dateString}/${datesToFetch}`, undefined, {
        retries: 0,
      });
      return resp.body;
    } catch (e) {
      return [];
    }
  }

  /**
   * Return schedule data for all scheduled entities in this destination
   * Eg. parks
   * @returns {array<object>}
   */
  async buildEntityScheduleData() {
    const now = this.getTimeNowMoment();
    const end = now.clone().add(30, 'days');

    // should be 7 or 30, but server crashes on random dates
    //  so we use 1 so at least we catch all the dates that don't crash
    const batchSize = 1;

    // loop through each 7 day block and fetch the calendar data for the next 30 days
    const calendarData = [];
    for (let i = now.clone(); i.isBefore(end); i.add(batchSize, 'days')) {
      const dateString = i.format('YYYY-MM-DD');
      const calendar = await this.fetchCalendarForDate(dateString, batchSize);
      calendarData.push(...calendar);
    }

    const times = [];

    calendarData.forEach((entry) => {
      if (entry.closed) return;
      const dateString = entry.dateRaw.slice(0, 10);
      const dateMoment = moment.tz(dateString, 'YYYY-MM-DD', this.config.timezone);

      const open = dateMoment.clone().hours(Number(entry.openingHoursDetailed.from)).minutes(0).seconds(0).milliseconds(0);
      const close = dateMoment.clone().hours(Number(entry.openingHoursDetailed.to)).minutes(0).seconds(0).milliseconds(0);

      times.push({
        date: dateString,
        type: "OPERATING",
        openingTime: open.format(),
        closingTime: close.format(),
      });

      // if we have evening hours, add these too
      if (entry.eveningEntranceFrom && entry.eveningEntranceFrom !== '00:00') {
        if (entry.eveningEntranceFrom.indexOf(':') >= 0) {
          const eveningOpen = dateMoment.clone().hours(Number(entry.eveningEntranceFrom.split(':')[0])).minutes(Number(entry.eveningEntranceFrom.split(':')[1])).seconds(0).milliseconds(0);
          if (eveningOpen.isBefore(close) && eveningOpen.isAfter(open)) {
            times.push({
              date: dateString,
              type: "INFO",
              description: "Evening Hours",
              openingTime: eveningOpen.format(),
              closingTime: close.format(),
            });
          }
        }
      }
    });

    return [
      {
        _id: 'lisebergpark',
        schedule: times,
      }
    ];
  }
}

import Ajv from 'ajv';
const ajv = new Ajv();

import {queueType, returnTimeState, statusType, boardingGroupState} from './parkTypes.js';

// queue schema, can be applied to various entity types
const queueSchema = {
  type: 'object',
  properties: {
    [queueType.standBy]: {
      type: 'object',
      properties: {
        waitTime: {
          type: ['integer', 'null'],
          minimum: 0,
        },
      },
      required: ['waitTime'],
    },
    // single rider is pretty much identical to standby
    [queueType.singleRider]: {
      type: 'object',
      properties: {
        waitTime: {
          type: ['integer', 'null'],
          minimum: 0,
        },
      },
      required: ['waitTime'],
    },
    [queueType.returnTime]: {
      type: 'object',
      properties: {
        returnStart: {
          // TODO - replace with regexed time? timestamp?
          type: ['string', 'null'],
        },
        returnEnd: {
          type: ['string', 'null'],
        },
        state: {
          type: 'string',
          enum: Object.values(returnTimeState),
        },
      },
      required: ['returnStart', 'returnEnd', 'state'],
    },
    [queueType.boardingGroup]: {
      type: 'object',
      properties: {
        allocationStatus: {
          type: 'string',
          enum: Object.values(boardingGroupState),
        },
        currentGroupStart: {
          type: ['string', 'integer', 'null'],
        },
        currentGroupEnd: {
          type: ['string', 'integer', 'null'],
        },
        nextAllocationTime: {
          type: ['string', 'null'],
        },
        estimatedWait: {
          type: ['integer', 'null'],
        },
      },
    },
  },
};

// showtimes schema, an array of show start times
const showtimesSchema = {
  type: 'array',
  items: {
    type: 'object',
    properties: {
      startTime: {
        type: ['string', 'null'],
        format: 'date-time',
      },
      endTime: {
        type: ['string', 'null'],
        format: 'date-time',
      },
      // freeform text to describe this schedule entry
      type: {
        type: 'string',
      },
    },
    required: ['startTime'],
  },
};

const statusSchema = {
  type: 'string',
  enum: Object.values(statusType),
};

// if any of these keys are present in livedata, we must run the schema validation against it
const schemas = {
  status: statusSchema,
  showtimes: showtimesSchema,
  queue: queueSchema,
  operatinghours: showtimesSchema, // TODO - uppercamel?
};

/**
 * Given an entity doc, and a live data object - validate the live data
 * @param {object} liveData
 * @return {Array<string>} Array of errors, of null if passes validation
 */
export function getLiveDataErrors(liveData) {
  if (liveData === undefined) return null;

  // find all keys that need validating
  const keys = Object.keys(schemas).filter((key) => {
    return !!liveData[key];
  });

  const errors = [];

  // test each schema-driven key and build an array of any errors
  keys.forEach((key) => {
    const validator = ajv.compile(schemas[key]);
    if (!validator(liveData[key])) {
      errors.push(...validator.errors);
    }
  });

  if (errors.length > 0) {
    return errors;
  }
  return null;
}

import { Destination } from '../destination.js';
import { attractionType, statusType, queueType, tagType, scheduleType, entityType } from '../parkTypes.js';
import moment from 'moment-timezone';

const queries = {
